import{_ as o,a as n}from"./008-BK48VWhW.js";import{_ as g}from"./009-B_xeZwXD.js";import{_ as r}from"./010-C_sEQWkL.js";import{_ as p}from"./011-BzP10_Dy.js";import{_ as i}from"./012-CnJZn6pa.js";import{_ as a}from"./013-BwkHrzI2.js";import{_ as l}from"./014-D72OM-cE.js";import{_ as e}from"./015-BRst6n3S.js";import{_ as m}from"./016-DbLG3bIh.js";import{_ as c,a as B}from"./018-YxP43UIO.js";import{_ as R}from"./019-CZPeNSOK.js";import{_ as E,c as v,a as d,d as t,e as s,b as w,w as C,r as b,o as U}from"./app-CUu1n7_5.js";const V="/parts-intellect-docs/assets/316-BRDDDfql.png",Q="data:image/png;base64,",u="/parts-intellect-docs/assets/318-DwAj4bJU.png",h="data:image/png;base64,",k="data:image/png;base64,",f="data:image/png;base64,",L="/parts-intellect-docs/assets/322-Bnsk1Osj.png",K="data:image/png;base64,",S="data:image/png;base64,",q="data:image/png;base64,",O="data:image/png;base64,",D="data:image/png;base64,",G="data:image/png;base64,",x="data:image/png;base64,",M="data:image/png;base64,",F="data:image/png;base64,",N="/parts-intellect-docs/assets/332-Ckj-ag58.png",W="/parts-intellect-docs/assets/333-D_4Odgvt.png",J="/parts-intellect-docs/assets/334-B0h6uiql.png",P="/parts-intellect-docs/assets/335-B9Oe5sNH.png",X="/parts-intellect-docs/assets/336-CBS4FjEa.png",Y="data:image/png;base64,",y="data:image/png;base64,",z="/parts-intellect-docs/assets/339-BFpHROuB.png",H="/parts-intellect-docs/assets/340-DkiYJ6rB.png",T="/parts-intellect-docs/assets/341-BnuvoKdL.png",j="/parts-intellect-docs/assets/342-DcPyNpX-.png",Z="/parts-intellect-docs/assets/343-C5nfASV0.png",_="/parts-intellect-docs/assets/344-BgGpCAI5.png",$="/parts-intellect-docs/assets/345-Clr7HQC6.png",AA={},tA={class:"hint-container info"};function sA(oA,A){const I=b("RouteLink");return U(),v("div",null,[A[5]||(A[5]=d('<p>Чтобы добавить нового клиента, выполните следующие действия:</p><p><strong>»</strong> В <strong>Главном меню</strong> выберите <strong>CRM ► Клиенты</strong> (Ctrl+K) отобразятся справочник <strong>Контрагентов</strong> категории <strong>Клиенты</strong>.</p><p><img src="'+V+'" alt=""></p><p><strong>»</strong> Нажмите кнопку <img src="'+Q+'" alt=""> на панели инструментов. Откроется окно добавления нового контрагента.</p><p><img src="'+u+'" alt=""></p><p><img src="'+o+'" alt=""> <strong>Вид контрагента</strong></p><p><img src="'+h+'" alt=""></p><p>Позволяет задать статус контрагента: <strong>Физическое лицо</strong> и <strong>Юридическое лицо</strong>. По умолчанию у всех новых и существующих контрагентов будет значение <strong>Физическое лицо</strong>;</p><p><img src="'+n+'" alt=""> <strong>ОПФ</strong></p><p><img src="'+k+'" alt=""></p><p>Позволяет задать организационно-правовую форму организации. Используется для <strong>Юридических лиц</strong>.</p><p><img src="'+g+'" alt=""> <strong>Город</strong></p><p><img src="'+f+'" alt=""></p><p>Позволяет задать город контрагента.</p><p><img src="'+r+'" alt=""> <strong>Наименование контрагента</strong></p><p><img src="'+L+'" alt=""></p><p>Позволяет задать полное наименование организации или ФИО клиента.</p><p><img src="'+p+'" alt=""> <strong>Отображать как</strong></p><p><img src="'+K+'" alt=""></p><p>Позволяет задать сокращенное название клиента. Данное наименование будет отображаться во всех документах.</p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Если поле <strong>Отображать как</strong> заполнено, то везде используется оно, иначе поле <strong>Наименование контрагента</strong>. Данный механизм удобно использовать для скрытия реального наименования поставщика от пользователей системы, либо для использования более широко употребляемого наименования.</p></div><p><img src="'+i+'" alt=""> <strong>Контрагент родитель</strong></p><p><img src="'+S+'" alt=""></p><p>Позволяет указать вышестоящего контрагента, указывается в случае подчиненности или представительства.</p><p><img src="'+a+'" alt=""> <strong>Мобильный телефон</strong></p><p><img src="'+q+'" alt=""></p><p>Позволяет указать контактный телефон контрагента.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Ввод мобильного телефона осуществляется согласно настройке <strong>Маска ввода по умолчанию для поля Мобильный телефон</strong>.Текущий номер телефона в поле может быть скрыт в соответствии с настройкой <strong>Отображать номера телефонов</strong>.</p></div><p><img src="'+l+'" alt=""> <strong>Может работать в интернет-магазине</strong></p><p><img src="'+O+'" alt=""></p><p>Позволяет установить признак участия клиента в синхронизации с Parts.Resource.</p><p><img src="'+e+'" alt=""> <strong>Тип контрагента</strong></p><p><img src="'+D+'" alt=""></p><p>Задается тип контрагента: <strong>Покупатель</strong>, <strong>Поставщик</strong>, <strong>Наша</strong> <strong>фирма</strong> или <strong>Банк</strong>. Для клиентов используется тип <strong>Покупатель</strong>.</p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>От типа контрагента зависит механизм расчета балансов контрагента, поэтому чтобы балансы рассчитывались корректно необходимо применять только один тип контрагента в карточке.</p></div><p><img src="'+m+'" alt=""> <strong>Код контрагента</strong></p><p><img src="'+G+'" alt=""></p><p>Присваивается автоматически при создании/сохранении контрагента.</p><p><img src="'+c+'" alt=""> <strong>Электронная почта</strong></p><p><img src="'+x+'" alt=""></p><p>Позволяет указать электронную почту контрагента.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Текущий адрес электронной почты в поле может быть скрыт в соответствии с настройкой <strong>Отображать адреса электронной почты</strong>.</p></div><p><img src="'+B+'" alt=""> <strong>Логин в Parts.Resource</strong></p><p><img src="'+M+'" alt=""></p><p>Позволяет указать логин от личного кабинета клиента на сайте Parts.Resource.</p><p><img src="'+R+'" alt=""> <strong>Сохранить и закрыть / Сохранить</strong></p><p><img src="'+F+'" alt=""></p><p>Позволяет сохранить и закрыть/сохранить карточку контрагента.</p><p><img src="'+N+'" alt=""></p><p><img src="'+o+'" alt=""> <strong>Финансы</strong></p><p><img src="'+W+'" alt=""></p><p>На вкладке задаются параметры:</p><ul><li><p><strong>Основная валюта</strong> – основная валюта контрагента, в которой вычисляются все его балансы;</p></li><li><p><strong>Национальная валюта</strong> – в данной валюте формируется <strong>Оборотно-сальдовая ведомость по контрагентам</strong> и выгружается данные в 1С;</p></li><li><p><strong>Выделять НДС</strong> – актуально для контрагента типа <strong>Наша фирма</strong>. При создании документа для поля &quot;Выделять НДС&quot; будет браться значение из карточки Нашей фирмы. Исключение составляют случаи создания документов <strong>Заказ поставщику</strong> и <strong>Приходная накладная</strong> из формы <strong>Проценки,</strong> тогда данное значение возьмется из карточки поставщика;</p></li><li><p><strong>Выделять НДС работах ЗН</strong> – опция доступна только для контрагента типа <strong>Наша фирма,</strong> и определяет выделять или нет НДС для работ в документах <strong>Заказ-Наряд</strong>;</p></li><li><p><strong>Счет по умолчанию</strong> – счет из справочника <strong>Счета и Кассы</strong>, который будет использоваться по умолчанию при формировании ПП и ППвх для контрагента;</p></li><li><p><strong>Отсрочка платежа, дн</strong> – количество дней, в течение которых задержка оплаты по накладной не является просроченной:</p><ul><li>для <strong>Расходной накладной</strong> и контрагентов типа <strong>Покупатель</strong> – количество дней для отсрочки оплаты <strong>Расходной накладной</strong>, по истечению которого долг по документу попадает в <strong>Баланс просроченный</strong>. Отсрочка используется при формировании <strong>Отчета о текущей дебиторской задолженности</strong> и <strong>Отчета о предстоящей дебиторской задолженности</strong>. Значение передается в документ расхода с возможностью его сменить;</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Право на изменение <strong>Отсрочки платежа</strong> в <strong>Расходных накладных</strong> определяется разрешением для пользователя в разделе <strong>Управление ► Роли пользователей ► Разрешения для роли пользователей ►</strong> группа <strong>Документы: Расходные накладные ►</strong> пункт <strong>Можно изменять отсрочку платежа</strong>.</p></div><ul><li>для <strong>Приходной накладной</strong> и контрагентов типа <strong>Поставщик</strong> – количество дней для отсрочки оплаты <strong>Приходной накладной</strong>. Значение используется при формировании <strong>Отчета о кредиторской задолженности</strong>;</li></ul></li><li><p><strong>Подсчет дней отсрочки платежа</strong> – определяет, как будет рассчитываться отсрочка по данному контрагенту. Доступны варианты:</p><ul><li><p><strong>Использовать рабочие дни</strong> – при расчете будут исключены выходные и праздничные дни, заданные для Нашей фирмы в разделе <strong>Управление ► Настройки программы ► Наши фирмы</strong> пункт <strong>Выходные дни</strong> и <strong>Праздничные дни</strong>;</p></li><li><p><strong>Использовать календарные дни</strong> – расчет будет произведен включая выходные и праздничные дни, заданные для Нашей фирмы;</p></li><li><p><strong>Использовать глобальное значение</strong> – будет использовано значение глобальной настройки <strong>Подсчет дней отсрочки платежа</strong> в разделе <strong>Управление ► Настройки программы ► Настройки</strong>, группа <strong>CRM</strong> <strong>► Контрагенты</strong>;</p></li></ul></li><li><p><strong>Минимальный % предоплаты по ЗК</strong> – определяет процент предоплаты, которую необходимо внести за заказ от данного клиента. Если поле не заполнено – по умолчанию используется значение из настройки <strong>Минимальный % предоплаты по ЗК</strong> из раздела <strong>Управление ► Настройки программы</strong> ► <strong>Настройки</strong>, группа <strong>CRM ► Контрагенты</strong>. При значении 0 ограничение на ввод предоплаты в заказе клиента снимается;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Для применения <strong>Минимального % предоплаты по ЗК</strong> необходимо также активировать опцию <strong>Запретить проводить ЗК без минимальной предоплаты</strong> (раздел <strong>Управление ► Настройки программы ► Роли пользователей ► Документы ► Заказы клиентов</strong>). При активном значении настройки проведение документа <strong>Заказ клиента</strong> без внесения предоплаты будет недоступно.</p></div><ul><li><p><strong>Лимит баланса по заказам –</strong> сумма, на которую можно провести <strong>Заказы клиента</strong> с учетом долга за предыдущие. Для возможности проведения заказов в долг, необходимо указать отрицательное значение лимита. Если значение <strong>Баланса по заказам</strong> окажется меньше, чем значение установленного лимита, то при проводке заказа выведется соответствующее уведомление. Лимит актуален только для контрагентов с типом <strong>Покупатель</strong>;</p></li><li><p><strong>Лимит баланса фактического –</strong> сумма, на которую можно провести <strong>Расходные накладные</strong> с учетом долга за предыдущие. Для возможности проведения расходов в долг, необходимо указать отрицательное значение лимита. Если значение <strong>Баланса фактического</strong> окажется меньше, чем значение установленного лимита, то при проводке расхода выведется соответствующее уведомление. Лимит актуален только для контрагентов с типом <strong>Покупатель</strong>.</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Запрет на проведение документов с учетом лимитов определяется опцией в разрешениях пользователя <strong>Можно проводить с превышением долга</strong> для <strong>Заказов клиентов</strong> и <strong>Расходных накладных</strong> (меню <strong>Управление ► Настройки программы ► Роли пользователей</strong> ► <strong>Разрешения для роли пользователей</strong> ► группа <strong>Документы ► Расходная накладная</strong> (для лимита фактического) и/или <strong>Заказ клиента</strong> (для лимита по заказам).</p></div><p><img src="'+n+'" alt=""> <strong>Реквизиты</strong></p><p><img src="'+J+'" alt=""></p>',58)),t("div",tA,[A[3]||(A[3]=t("p",{class:"hint-container-title"},"Примечание",-1)),A[4]||(A[4]=t("p",null,[s("В карточке контрагента доступно автоматическое заполнение реквизитов по "),t("strong",null,"ИНН"),s(" и "),t("strong",null,"БИК"),s(" через команду "),t("strong",null,"Обновить реквизиты"),s(" на панели инструментов.")],-1)),t("p",null,[A[1]||(A[1]=s("Алгоритм заполнения реквизитов в карточке описан в разделе ")),w(I,{to:"/work/klienty/dobavlenie/zapolnenie_rekvizitov/zapolnenie_rekvizitov_v_kartochke_kontragenta.html"},{default:C(()=>A[0]||(A[0]=[s("Заполнение реквизитов по ИНН")])),_:1,__:[0]}),A[2]||(A[2]=s("."))])]),A[6]||(A[6]=d('<p>На вкладке задаются параметры:</p><ul><li><p><strong>Директор, Главный бухгалтер</strong> – данные (ФИО) директора и бухгалтера организации;</p></li><li><p><strong>ФИО ИП</strong> – Фамилия Имя Отчество индивидуального предпринимателя;</p></li><li><p><strong>Рабочий телефон, Факс, ICQ, Skype, Web-сайт, Дополнительные email</strong> – реквизиты и контактные данные организации;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Ввод телефона осуществляется согласно настройке <strong>Маска ввода по умолчанию для поля Мобильный телефон</strong>.Текущий номер телефона в поле может быть скрыт в соответствии с настройкой <strong>Отображать номера телефонов</strong>.</p></div><ul><li><p><strong>ИНН</strong>, <strong>Дата регистрации</strong>, <strong>КПП, ОГРН</strong>, <strong>Дата выдачи ОГРН</strong>, <strong>ОКАТО</strong>, <strong>ЕГРН</strong>, <strong>ОКПО</strong>, <strong>ОКДП</strong>, <strong>№ свидетельства плательщика НДС</strong> – учетные данные организации;</p></li><li><p><strong>Основной ОКВЭД</strong> – код основного вида экономической деятельности компании;</p></li><li><p><strong>Статус организации</strong> – состояние дееспособности организации, доступные значения: <strong>Действующая</strong>, <strong>Ликвидируется</strong>, <strong>Ликвидирована</strong>, <strong>Реорганизация</strong>, <strong>Банкротство</strong>;</p></li><li><p><strong>Внешний идентификатор</strong> – внешний идентификатор контрагента;</p></li><li><p><strong>Сектор на карте</strong> – сектор на карте (используется в качестве фильтра в <strong>ABC-анализе по Контрагентам</strong>);</p></li><li><p><strong>Адрес фактический</strong>, <strong>Адрес юридический</strong> – фактический и юридический адрес расположения организации.</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Для типа контрагента <strong>Банк</strong> в карточке контрагента на вкладке <strong>Реквизиты</strong>, появляются дополнительные обязательные поля для заполнения <strong>БИК*</strong>, <strong>Корр. Счет*</strong> и <strong>МФО банка</strong>.</p></div><p><img src="'+g+'" alt=""> <strong>Клиент</strong></p><p><img src="'+P+'" alt=""></p><p>На вкладке задается основная информация о покупателе:</p><ul><li><p><strong>Место комплектации</strong> – позволяет выбрать склад/тт по умолчанию для контрагента, которое будет использоваться при создании документа <strong>Перемещения по местам комплектации клиентов</strong>;</p></li><li><p><strong>Место выдачи</strong> – наиболее удобная для выдачи товара торговой точки, заполнение не обязательно;</p></li><li><p><strong>Способ доставки по умолчанию</strong> – значение по умолчанию, которое будет подставляться в документы по данному контрагенту в поле <strong>Способ доставки;</strong></p></li><li><p><strong>Грузополучатель по умолчанию</strong> – грузополучатель будет по умолчанию подставляться в документы Расходная накладная;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Для контрагентов типа <strong>Поставщик</strong> опция <strong>Грузополучатель по умолчанию</strong> доступна на вкладке <strong>Поставщик</strong>, указанный для контрагента грузополучатель будет по умолчанию подставляться в документы <strong>Возврат поставщику</strong>.</p></div><ul><li><strong>Адрес доставки</strong> – содержит данные об адресе доставки заказов клиента;</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Данные из полей <strong>Способ доставки по умолчанию</strong> и <strong>Адрес доставки</strong> автоматически подставляются в документы при выборе контрагента. Данные подставляются, если они заполнены в карточке контрагента.</p></div><ul><li><p><strong>Статус клиента</strong> – статус клиента по словарю <strong>Статусы клиента</strong>;</p></li><li><p><strong>RFM-Сегмент</strong> – значение RFM-сегмента, присвоенное контрагенту в результате <strong>RFM-анализа</strong>;</p></li><li><p><strong>RFM-Группа</strong> – RFM-группа, присвоенная контрагенту в результате <strong>RFM-анализа</strong>;</p></li><li><p><strong>Ответственный менеджер</strong> – пользователь, сопровождающий клиента. По умолчанию это автор карточки контрагента;</p></li><li><p><strong>Номер дисконтной карты</strong> – номер, который используется для быстрого поиска и вставки контрагента в документах и форме <strong>Быстрой продажи</strong>;</p></li><li><p><strong>Документ, удостоверяющий личность</strong> – данные персонального документа клиента;</p></li><li><p><strong>ФИО и Должность</strong> – данные контактного лица организации;</p></li><li><p><strong>Номер и Дата</strong> – данные договора между Нашей фирмой и контрагентом, которые могут быть использованы в печатных формах документов.</p></li></ul><p><img src="'+r+'" alt=""> <strong>Категория клиента</strong></p><p><img src="'+X+'" alt=""></p><p>На вкладке задается:</p><ul><li><p><strong>Актуальная категория</strong> – текущая категория, к которой относится клиент;</p></li><li><p><strong>Тип категории –</strong> выберите тип категории клиента.</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>При использовании динамической категории клиентов необходимо выполнить необходимые настройки для <strong>Накопительной системы скидок</strong>.</p></div><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Для типа контрагента <strong>Поставщик</strong> в карточке контрагента появляется дополнительная вкладка <strong>Поставщик</strong>, которая позволяет выбрать  <strong>Грузополучателя по умолчанию</strong> и задать <strong>Прайс-лист из наличия по умолчанию</strong> для приходных накладных от данного поставщика.</p></div><p><img src="'+p+'" alt=""> <strong>Прочее</strong></p><p><img src="'+Y+'" alt=""></p><p>На вкладке можно указать дополнительную информацию о контрагенте.</p><p><img src="'+i+'" alt=""> <strong>Автомобили</strong></p><p><img src="'+y+'" alt=""></p><p><img src="'+z+'" alt=""></p><p>На вкладке можно выбрать или добавить автомобили, закрепленные за контрагентом.</p><p><img src="'+a+'" alt=""> <strong>Документы</strong></p><p><img src="'+H+'" alt=""></p><p>На вкладке отображаются документы и позиции в них, в которых фигурирует данный контрагент.</p><p><img src="'+l+'" alt=""> <strong>История блокировок клиента</strong></p><p><img src="'+T+'" alt=""></p><p>На вкладке отображается история всех блокировок клиента.</p><p><img src="'+e+'" alt=""> <strong>Уведомления</strong></p><p><img src="'+j+'" alt=""></p><p>На вкладке можно установить индивидуальные настройки для каждого типа уведомления в программе для получения контрагентом email или SMS уведомлений. Если в карточке контрагента не будут установлены индивидуальные параметры получения уведомлений, то будут использоваться значения из <strong>Настроек программы</strong>. Также существует возможность для каждого уведомления указать индивидуальный почтовый адрес клиента, отличный от его основного email из карточки. Настройка уведомлений в системе описана более подробно в разделе Информационная рассылка.</p><p>На вкладке <strong>Уведомления</strong> присутствует возможность копирования настроек уведомлений от одного контрагента другим. Для копирования доступны настройки всех SMS и/или email уведомлений, а также конкретного SMS или email уведомления. Для копирования настроек уведомлений выполните следующие действия:</p><p><strong>»</strong> На панели инструментов выполните команду <strong>Скопировать настройки</strong>;</p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Данная возможность доступна только пользователю с ролью Admin, дополнительно для пользователя в разделе <strong>Управление ►</strong> <strong>Настройки программы ► Роли пользователей ► Разрешения для роли пользователей ► Справочники ► Контрагенты</strong> ► блок <strong>Редактирование</strong> в поле <strong>Можно изменять</strong> должно стоять разрешение на изменение записей.</p></div><p><strong>»</strong> В справочнике <strong>Контрагенты</strong> Выберите одного или нескольких контрагентов, которым необходимо скопировать настройки из карточки текущего контрагента. Нажмите кнопку <strong>Выбрать</strong> для переходя к следующему шагу;</p><p><img src="'+Z+'" alt=""></p><p><strong>»</strong> В открывшемся окне, укажите параметры копирования. Нажмите кнопку <strong>ОК</strong> для запуска копирования настроек;</p><p><img src="'+_+'" alt=""></p><p><strong>»</strong> По результатам копирования выведется всплывающее уведомление. Если по какой-либо причине контрагент был пропущен, откроется файл журнала с причиной пропуска.</p><p><img src="'+$+'" alt=""></p><p><img src="'+m+'" alt=""> <strong>Список платежей</strong></p><p>Вкладка <strong>Список платежей</strong> отображает список платежных документов, в которых фигурирует контрагент</p><p><img src="'+c+'" alt=""> <strong>Изображения</strong></p><p>Вкладка позволяет прикрепить изображения к карточке контрагента</p><p><strong>»</strong> Нажмите кнопку <strong>Сохранить и закрыть (F2)</strong> для сохранения карточки контрагента.</p>',49))])}const BA=E(AA,[["render",sA]]),RA=JSON.parse('{"path":"/work/klienty/dobavlenie/dobavlenie_novogo_klienta.html","title":"Добавление нового клиента","lang":"en-US","frontmatter":{"title":"Добавление нового клиента","createTime":"2025/05/21 12:00:44"},"headers":[],"readingTime":{"minutes":5.63,"words":1688},"git":{"updatedTime":1748274032000,"contributors":[{"name":"bONK-cup","username":"bONK-cup","email":"kokosik260302@gmail.com","commits":2,"avatar":"https://avatars.githubusercontent.com/bONK-cup?v=4","url":"https://github.com/bONK-cup"}]},"filePathRelative":"work/klienty/dobavlenie/dobavlenie_novogo_klienta.md","categoryList":[{"id":"67e92c","sort":10000,"name":"work"},{"id":"6faae8","sort":10012,"name":"klienty"},{"id":"de8155","sort":10013,"name":"dobavlenie"}]}');export{BA as comp,RA as data};
