import{_ as A,a as t}from"./008-BK48VWhW.js";import{_ as g}from"./009-B_xeZwXD.js";import{_ as s}from"./010-C_sEQWkL.js";import{_ as r}from"./011-BzP10_Dy.js";import{_ as i,c as p,a as n,o as a}from"./app-CUu1n7_5.js";const e="/parts-intellect-docs/assets/037-CXJfBYcM.png",I="/parts-intellect-docs/assets/038-D4WhGajW.png",B="data:image/png;base64,",m="data:image/png;base64,",E="data:image/png;base64,",c="data:image/png;base64,",l="/parts-intellect-docs/assets/043-Bw_S_1Ja.png",C="data:image/png;base64,",Q="data:image/png;base64,",R="data:image/png;base64,",d="data:image/png;base64,",v="/parts-intellect-docs/assets/048-DQlxgvPE.png",u="data:image/png;base64,",k="data:image/png;base64,",b="data:image/png;base64,",w="/parts-intellect-docs/assets/052-Dt7B-BaC.png",h="/parts-intellect-docs/assets/053-D0XU_Mwc.png",X="data:image/png;base64,",V="/parts-intellect-docs/assets/dobavlenie_skladov_i_torgovykh_tochek_1-kDsI_bmw.png",K="data:image/png;base64,",S="data:image/png;base64,",M="/parts-intellect-docs/assets/dobavlenie_skladov_i_torgovykh_tochek_2-DkpN4eUB.png",D="data:image/png;base64,",O="data:image/png;base64,",q="data:image/png;base64,",W={};function N(U,o){return a(),p("div",null,o[0]||(o[0]=[n('<p>Чтобы создать новую торговую точку или склад, выполните следующие действия:</p><p><strong>»</strong> В <strong>Главном меню</strong> выберите раздел <strong>Склад и закупки ► Склады/Торговые точки</strong>.</p><p><img src="'+e+'" alt=""></p><p><strong>»</strong> Нажмите кнопку <strong>Новый</strong> и выберите тип создаваемой торговой точки/склада:</p><ul><li><p><strong>Торговая точка/Склад</strong> – магазин, филиал или склад, который может иметь своей наличие и использоваться для формирования приходной накладной, участвовать как &quot;место выдачи&quot; для заказов клиентов. Для данного типа можно создавать подчиненные <strong>Склады</strong> и <strong>Места хранения</strong>, а также заполнять дополнительные <strong>Реквизиты</strong> (адрес, расписание работы и пр.);</p></li><li><p><strong>Склад</strong> – склад для хранения товаров, может иметь своей наличие и использоваться для формирования приходной накладной, участвовать как &quot;место выдачи&quot; для заказов клиентов. В подчинении может иметь только <strong>Места хранения</strong>;</p></li><li><p><strong>Место хранения</strong> – место хранения всегда подчинено какому-либо склад или торговой точке. Места хранения используются для структурированного расположения товара на складе;</p></li><li><p><strong>Склад клиента</strong> – склад клиента участвует в документах <strong>Отгрузки на реализацию</strong>.</p></li></ul><p><strong>»</strong> В окне откроется окно инспектора для добавления новой торговой точки/склада.</p><p>На вкладке <strong>Склад/Торговая точка</strong> заполните следующие поля:</p><p><img src="'+I+'" alt=""></p><p><img src="'+A+'" alt=""> <strong>Название</strong></p><p><img src="'+B+'" alt=""></p><p>Позволяет заполнить наименование торговой точки.</p><p><img src="'+t+'" alt=""> <strong>Идентификатор места хранения</strong></p><p><img src="'+m+'" alt=""></p><p>Позволяет задать краткий символьный идентификатор склада или места хранения. Данное значение удобно использовать в документах и печатных бланках, для быстрого определения места расположения товара.</p><p>Например:</p><div class="hint-container note"><p class="hint-container-title">Пример</p><p>Место хранения товара вида <em>Склад Москва – Стеллаж 12 – полка 3</em>, можно отобразить на этикетках как <em>MSK-12-3</em></p></div><p><img src="'+g+'" alt=""><strong>Группа</strong></p><p><img src="'+E+'" alt=""></p><p>Позволяет, при использовании типа <strong>Склад</strong> или <strong>Место хранения</strong>, выбрать основной склад/торговую точку.</p><p><img src="'+s+'" alt=""><strong>Тип объекта</strong></p><p><img src="'+c+'" alt=""></p><p>Поле заполняется автоматически и отражает выбранный тип склада/торговой точки.</p><p><strong>»</strong> Вкладка <strong>Реквизиты</strong> служит для внесения дополнительной информации по торговой точке, которую можно использовать в печатных формах.</p><p>Заполните следующие поля на вкладке:</p><p><img src="'+l+'" alt=""></p><p><img src="'+A+'" alt=""><strong>Контакты</strong></p><p><img src="'+C+'" alt=""></p><p>Позволяет задать контактные данные торговой точки/склада.</p><p><img src="'+t+'" alt=""><strong>Адрес</strong></p><p><img src="'+Q+'" alt=""></p><p>Позволяет заполнить адрес магазина/склада.</p><p><img src="'+g+'" alt=""><strong>Описание</strong></p><p><img src="'+R+'" alt=""></p><p>Позволяет добавить описание торговой точки.</p><p><img src="'+s+'" alt=""><strong>Сохранить и закрыть</strong></p><p><img src="'+d+'" alt=""></p><p>Позволяет сохранить внесенные изменения и закрыть окно инспектора. Новая запись отобразится в таблице справочника.</p><p>В системе возможно создание многоуровневой структуры складов. Для этого необходимо:</p><p><strong>»</strong> В <strong>Главном меню</strong> выберите раздел <strong>Склад и закупки ► Склады/Торговые точки</strong>.</p><p><strong>»</strong> Нажмите кнопку <strong>Новый</strong> и выберите тип создаваемой записи <strong>Место хранения</strong>.</p><p><strong>»</strong> В окне инспектора в качестве <strong>Названия</strong> указывается сектор склада или место расположения, а в поле <strong>Группа</strong> выбирается основной объект (склад/тт/другой сектор, сектор на складе), к которому относится место хранения.</p><p>Создать сразу несколько мест хранения на складе (стеллажи, полки и т.п.) можно с использованием инструмента <strong>Конструктор мест хранения</strong>. Для этого:</p><p><strong>»</strong> Выделите в списке торговую точку/склад для которого хотите создать места хранения, нажмите кнопку <strong>Новый</strong> и выберите команду <strong>Массовое создание мест хранения</strong>. Откроется окно конструктора.</p><p><img src="'+v+'" alt=""></p><p><img src="'+A+'" alt=""><strong>Добавить/удалить</strong></p><p><img src="'+u+'" alt=""></p><p>Позволяют добавить или удалить новое место хранения для <strong>Выбранного склада</strong>.</p><p><img src="'+t+'" alt=""><strong>Список добавленных мест хранения</strong></p><p>Позволяет для добавленного места хранения задать:</p><ul><li><p><strong>Наименование МХ</strong> – название места хранения;</p></li><li><p><strong>Количество МХ</strong> – количество позиций добавленного места хранения на <strong>Выбранном складе</strong>;</p></li><li><p><strong>Тип обозначения</strong> – тип обозначения мест хранения;</p></li><li><p><strong>Начальное значение</strong> – указывается начальное значение нумерации места хранения.</p></li></ul><p><img src="'+g+'" alt=""><strong>Разделитель</strong></p><p><img src="'+k+'" alt=""></p><p>Позволяет указать символ, который будет разделять значения мест хранения при формировании идентификатора.</p><p><img src="'+s+'" alt=""><strong>Создать</strong></p><p><img src="'+b+'" alt=""></p><p>Позволяет создать добавленные места хранения и завершить работу <strong>Конструктора мест хранения</strong>.</p><p>На примере показанном ниже, после нажатия на кнопку <strong>Создать</strong> для склада &quot;Торговая точка&quot; будет создано 5 стеллажей, по 2 полки и на каждой полке по 2 ячейки.</p><p><img src="'+w+'" alt=""></p><p>Для каждого места хранения сгенерирован идентификатор в соответствии с правилами конструктора: у ячеек обозначение латинскими буквами, у остальных мест хранения цифрами, разделитель &quot;_&quot;.</p><p><img src="'+h+'" alt=""></p><p><img src="'+A+'" alt=""><strong>Печать</strong></p><p><img src="'+X+'" alt=""></p><p>Позволяет осуществить печать этикеток для выбранных и подчиненных им мест хранения, на которых можно вывести идентификатор места хранения в виде штрихкода, используя кодировку.</p><p><img src="'+V+'" alt=""></p><p><img src="'+K+'" alt=""></p><p><img src="'+t+'" alt=""><strong>Отчеты</strong></p><p><img src="'+S+'" alt=""></p><p>Позволяет создать отчет о движении и наличии товаров на выбранном месте хранения.</p><p><img src="'+M+'" alt=""></p><p><img src="'+g+'" alt=""><strong>Прочее</strong></p><p><img src="'+D+'" alt=""></p><p>Позволяет <strong>Создать задание на инвентаризацию</strong>, для выбранного места хранения, а так же <strong>Открыть задание на инвентаризацию в работе</strong>, при его наличии.</p><p><img src="'+s+'" alt=""><strong>Править</strong></p><p><img src="'+O+'" alt=""></p><p>Позволяет открыть инспектор редактирования торговой точки, который позволяет изменить ее параметры.</p><div class="hint-container warning"><p class="hint-container-title">Внимание!</p><p>После изменения <strong>Названия</strong> места хранения для обновления значения в колонке <strong>Полный путь</strong> доступна команда <strong>Прочее ► Обновить полный путь склада/торговой точки</strong>.</p></div><p><img src="'+r+'" alt=""><strong>Действие</strong></p><p><img src="'+q+'" alt=""></p><p>При помощи кнопки <strong>Открыть мастер заказов на склад</strong>, позволяет осуществить вызов <strong>Мастера заказов</strong> на склад для торговых точек и складов.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Помимо складов фирмы, можно создать склады клиента (клиент или партнер в данном случае занимается реализацией товара).</p></div>',80)]))}const H=i(W,[["render",N]]),Y=JSON.parse('{"path":"/work/svoe_nalichie/dobavlenie_skladov_i_torgovykh_tochek.html","title":"Добавление складов и торговых точек","lang":"en-US","frontmatter":{"title":"Добавление складов и торговых точек","createTime":"2025/05/14 22:09:06"},"headers":[],"readingTime":{"minutes":2.36,"words":708},"git":{"updatedTime":1751574221000,"contributors":[{"name":"bONK-cup","username":"bONK-cup","email":"kokosik260302@gmail.com","commits":2,"avatar":"https://avatars.githubusercontent.com/bONK-cup?v=4","url":"https://github.com/bONK-cup"},{"name":"a.lutoshkin","username":"","email":"a.lutoshkin@tradesoft.ru","commits":1,"avatar":"https://gravatar.com/avatar/605ee9d365e3a6380f8e9c71f9269ee5373d537aebefbece4e023cac396dfc8d?d=retro"}]},"filePathRelative":"work/svoe_nalichie/dobavlenie_skladov_i_torgovykh_tochek.md","categoryList":[{"id":"67e92c","sort":10000,"name":"work"},{"id":"2fc035","sort":10076,"name":"svoe_nalichie"}]}');export{H as comp,Y as data};
