import{_ as t,a as n}from"./008-BK48VWhW.js";import{_ as o}from"./009-B_xeZwXD.js";import{_ as r}from"./010-C_sEQWkL.js";import{_ as g}from"./011-BzP10_Dy.js";import{_ as A}from"./012-CnJZn6pa.js";import{_ as p}from"./013-BwkHrzI2.js";import{_ as i,c as l,a,o as e}from"./app-CUu1n7_5.js";const c="/parts-intellect-docs/assets/135-Bzs5v30B.png",m="/parts-intellect-docs/assets/136-BORbZOub.png",u="/parts-intellect-docs/assets/137-C2PX7i2v.png",v="/parts-intellect-docs/assets/138-IRjOipIP.png",d="data:image/png;base64,",U="/parts-intellect-docs/assets/140-E8Q3D3eo.png",B="/parts-intellect-docs/assets/141-DH_leDT-.png",h="data:image/png;base64,",R="/parts-intellect-docs/assets/143-BUVK-iWs.png",b="data:image/png;base64,",T="/parts-intellect-docs/assets/145-aOJKefRB.png",N="data:image/png;base64,",w="/parts-intellect-docs/assets/147-DL9J--tJ.png",Q="data:image/png;base64,",X="/parts-intellect-docs/assets/149-CFaoy6UJ.png",E="/parts-intellect-docs/assets/150-CMYqM0YL.png",V="data:image/png;base64,",L="/parts-intellect-docs/assets/152-DMQjvdB-.png",P="data:image/png;base64,",f="/parts-intellect-docs/assets/154-CnrN7GE7.png",C="data:image/png;base64,",K="data:image/png;base64,",S="data:image/png;base64,",z="data:image/png;base64,",M="data:image/png;base64,",O="/parts-intellect-docs/assets/160-DW9h-KPq.png",Z="/parts-intellect-docs/assets/161-Scx0DB4-.png",G="/parts-intellect-docs/assets/162-Cx0gzvFa.png",W="/parts-intellect-docs/assets/163-Dqo-_Yqf.png",y="/parts-intellect-docs/assets/164-CYLLiGRJ.png",k="/parts-intellect-docs/assets/165-CNHke4EZ.png",D={};function q(Y,s){return e(),l("div",null,s[0]||(s[0]=[a('<p><strong>Мастер заказов на склад</strong> (МЗМО) позволяет сформировать документ <strong>Наш заказ</strong> <strong>на склад</strong> для пополнения остатков на складах. Вызов мастера осуществляется с помощью команды <strong>Открыть мастер заказов на склад</strong>, из:</p><ul><li>справочника <strong>Товары</strong> по выделенным позициям, либо по <strong>Группе товара</strong>;</li></ul><p><img src="'+c+'" alt=""></p><ul><li>справочника <strong>Прайс-листы наличия</strong> для <strong>Гибкой</strong> СЦО;</li></ul><p><img src="'+m+'" alt=""></p><ul><li>справочника <strong>Склады/Торговые точки</strong>.</li></ul><p><img src="'+u+'" alt=""></p><p>Мастер оперирует значениями <strong>Минимальный заказ/Точка заказа</strong> у товаров, остатками и уже заказанным количеством (товары заказаны у поставщика) по каждому товару в рамках анализируемого склада. Для формирования заказа на недостающий товар на складе с помощью <strong>Мастера заказов</strong> <strong>на склад</strong> необходимо:</p><p><strong>»</strong> Запустить <strong>Мастер заказов на склад</strong> из необходимого раздела.</p><p><strong>»</strong> Программа отобразит перечень позиций, сгруппированных по коду/производителю. Данные в колонки табличной части заносятся по мере выполнения основных шагов, расположенных на панели инструментов мастера.</p><p><strong>»</strong> Перед тем как выполнить основные шаги в левой части окна установите необходимые настройки и параметры для работы мастера:</p><p><img src="'+v+'" alt=""></p><p><img src="'+t+'" alt=""> <strong>Табличная часть</strong></p><p>Табличная часть содержит блок колонок <strong>Товар</strong> со следующими данными, которые заполняются уже при запуске мастера:</p><ul><li><p><strong>Вид группы</strong> – отображает включенные в настройках мастера <strong>Виды групп</strong> позиций после выполнения шага <strong>Определить цены</strong>,</p></li><li><p><strong>Группа п/п</strong> – номер группы позиции по порядку.</p></li><li><p><strong>Группа товара</strong> – группа товара по справочнику.</p></li><li><p><strong>Код и Внутренний код, Производитель, Исходный производитель, Название и Альтернативное название</strong> – данные о товаре из справочника.</p></li><li><p><strong>Количество дней с последней продажи</strong> – количество дней с последней продажи товара (учитывая все склады и наши фирмы). Колонка заполняется после <strong>Определения цен</strong> на позиции.</p></li><li><p><strong>Последняя цена закупа</strong> – цена закупа на товар из последнего прихода (учитывая все склады и наши фирмы). Колонка заполняется после <strong>Определения цен</strong> на позиции.</p></li><li><p><strong>Последняя цена продажи</strong> – цена продажи из последней отгрузки товара (учитывая все склады и наши фирмы). Колонка заполняется после <strong>Определения цен</strong> на позиции.</p></li></ul><p><img src="'+n+'" alt=""> <strong>Минимальный запас/Точка заказа</strong></p><p><img src="'+d+'" alt=""></p><p>Блок содержит настройки:</p><ul><li><p><strong>Анализируемый склад</strong> <strong>–</strong> склад, для которого считывается текущее наличие товара и значения Минимальные остатки/Точка заказа из карточек товара.</p></li><li><p><strong>Расчет МЗ-ТЗ</strong> – определяет способ расчета минимального запаса и точки заказа товара:</p><ul><li><p><strong>Автоматически</strong> – значение минимального запаса и точки заказа будет рассчитываться на основании продаж товара автоматически.</p></li><li><p><strong>Из карточки товара</strong> – значение минимального запаса и точки заказа будет наследоваться из карточки товара, с вкладки <strong>Минимальные остатки и места хранения</strong>.</p></li></ul></li></ul><p>При выборе пункта <strong>Автоматически</strong> в <strong>Расчет МЗ-ТЗ</strong> доступен ряд настроек, влияющих на результаты расчетов:</p><p><img src="'+U+'" alt=""></p><ul><li><p><strong>Период для анализа продаж, по которому будет рассчитываться МЗ-ТЗ</strong> – позволяет рассчитать количество товара к заказу, анализируя данные за выбранный период:</p><ul><li><strong>Ближайшие дни</strong> – для анализа используется прошедший период, сформированный от даты, предшествующей текущей (дата вчерашнего дня) по продолжительности равный значению настройки <strong>Пополнить запасы товара на, дней</strong>;</li></ul><div class="hint-container note"><p class="hint-container-title">Пример</p><p>Текущая дата – 31.12.2020.</p><p>Пополнить запасы товара на, дней = 10.</p><p>Период для анализа – 21.12.2020-30.12.2020.</p></div><ul><li><strong>Прошлый год</strong> – для анализа используется период предыдущего года, равный по датам периоду, начиная с Желаемая дата поставки (только день и месяц, год используется предыдущий) и продолжительностью значения настройки <strong>Пополнить запасы товара на, дней</strong>;</li></ul><div class="hint-container note"><p class="hint-container-title">Пример</p><p>Желаемая дата поставки – 07.01.2021.Пополнить запасы товара на, дней = 10.Период для анализа – 07.01.2020-16.01.2020.</p></div></li><li><p><strong>Желаемая дата поставки</strong> – дата, с которой начинается период, заданный в настройке <strong>Пополнить запасы товара на, дней</strong>, соответствует дате, с которой планируется начало продаж товара;</p></li><li><p><strong>Пополнить запасы товара на, дней</strong> – период времени, для которого будет рассчитано среднее количество продаж за анализируемый промежуток времени, соответствует количеству дней, в течение которого планируется продавать товар со склада (с даты, заданной настройкой Желаемая дата поставки) до следующей поставки;</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Устанавливается значение от 1 до 365 дней.</p></div><ul><li><strong>Анализ динамики продаж, дней</strong> – период вычисления коэффициента динамики продаж, помогает определить динамику роста, либо падения продаж в будущем. При использовании настройки среднее количество продаж за период будет умножаться на значение настройки <strong>Пополнить запасы товара на, дней</strong> и на коэффициент динамики продаж. Количество дней, заданное в настройке, определит продолжительность периода для анализа данных;</li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Устанавливается значение от 1 до 365 дней.</p></div><div class="hint-container info"><p class="hint-container-title">Примечание</p><p><strong>Коэффициент динамики продаж</strong> – отношение количества продаж за текущий период, указанный в настройке <strong>Пополнить запасы товара на, дней</strong> к предыдущему. Значение коэффициента не может быть меньше <strong>0.5</strong> и больше <strong>2</strong>.</p></div><ul><li><p><strong>Период для анализа продаж, по которому будет рассчитываться МЗ-ТЗ</strong> – влияет на способ расчета динамики продаж:</p><ul><li><strong>Ближайшие дни</strong> – для анализа используется два прошедших периода. Первый период (текущий) формируется от даты, предшествующей текущей (дата вчерашнего дня) по продолжительности равный значению настройки <strong>Анализ динамики продаж, дней</strong>. Второй период (предыдущий) формируется от даты, предшествующей дате начала первого периода по продолжительности равный значению настройки <strong>Анализ динамики продаж, дней</strong>.</li></ul><div class="hint-container note"><p class="hint-container-title">Пример</p><p>Текущая дата – 31.12.2020.</p><p>Анализ динамики продаж, дней – 90.</p><p>Текущий период для анализа – 02.10.2020-30.12.2020.</p><p>Предыдущий период для анализа – 04.07.2020-01.10.2020.</p></div><ul><li><strong>Прошлый год</strong> – для анализа используется два прошедших периода (промежуток времени в этом и прошлом году). Первый период (текущий) формируется от даты, предшествующей текущей (дата вчерашнего дня) по продолжительности равный значению настройки <strong>Анализ динамики продаж, дней</strong>. Второй период (предыдущий) формируется от даты, предшествующей текущей, но в прошлом году, по продолжительности равный значению настройки <strong>Анализ динамики продаж, дней</strong>.</li></ul><div class="hint-container note"><p class="hint-container-title">Пример</p><p>Текущая дата – 31.12.2020.</p><p>Анализ динамики продаж, дней – 90.</p><p>Текущий период для анализа – 02.10.2020-30.12.2020.</p><p>Предыдущий период для анализа – 02.10.2019-30.12.2019.</p></div></li><li><p><strong>Вычитать товары под заказ</strong> – определяет, будут ли учитываться продажи заказных товаров при подсчете среднего количества продаж. При активации настройки позволяет минимизировать товарные запасы, вычитая из количества проданного товара количество, привезенное под заказ покупателя;</p></li><li><p><strong>Прибавлять неудовлетворенный спрос</strong> – определяет, будет ли учитываться неудовлетворенный спрос клиентов при подсчете среднего количества продаж. При активации настройки позволяет восполнить неудовлетворенный покупательский спрос. Прибавляет к количеству проданного, товары, добавленные менеджером в неудовлетворенный спрос (формируется в проценке вручную);</p></li><li><p><strong>Рассчитать МЗ, как если бы товар каждый день был в наличии и продавался</strong> – при расчетах из заданного периода исключаются те дни, когда товар не продавался по причине отсутствия на складе. Настройка рассчитает продажи, как если бы товар каждый день периода был на складе и продавался бы с такой же периодичностью, как продавался фактически. За счет этого происходит повышение среднего уровня продаж за период;</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Единовременно может использоваться только одна из настроек <strong>Прибавлять неудовлетворенный спрос</strong> или <strong>Рассчитать МЗ, как если бы товар каждый день был в наличии и продавался</strong>.</p></div><ul><li><p><strong>Приравнять к среднему кол-во товара в РН, если он превышает среднее более чем в 2 раза</strong> – позволяет повысить точность расчетов при использовании настройки <strong>Рассчитать МЗ, как если бы товар каждый день был в наличии</strong> <strong>и продавался</strong>. Если за выбранный период есть расходные накладные и заказ-наряды, в которых количество проданных единиц товара превышает среднее значение за год более чем в 2 раза, то для них при расчетах количество будет уменьшено до среднего значения за год;</p></li><li><p><strong>Точка заказа товара от МЗ, %</strong> – позволяет осуществить автоматический расчет значения <strong>Точка заказа</strong> как заданный процент от значения <strong>Минимальный запас</strong> с округлением до целого в большую сторону;</p></li></ul><p>При выборе пункта <strong>Из карточки товара</strong> в <strong>Расчет МЗ-ТЗ</strong> доступен ряд настроек:</p><p><img src="'+B+'" alt=""></p><ul><li><p><strong>Исходя из текущего сезона</strong> – значение минимального запаса и точки заказа будет наследоваться из группы колонок <strong>Высокий спрос</strong> либо <strong>Низкий спрос</strong>. Группа колонок определяется на основании того, к какой группе месяцев высокого/низкого спроса на вкладке <strong>Сезонность товара</strong> относится текущий месяц.</p></li><li><p><strong>Для Высокого спроса</strong> – значение минимального запаса и точки заказа будет наследоваться из группы колонок <strong>Высокий спрос</strong>;</p></li><li><p><strong>Для Низкого спроса</strong> – значение минимального запаса и точки заказа будет наследоваться из группы колонок <strong>Низкий спрос</strong>.</p></li></ul><p><img src="'+o+'" alt=""> <strong>Рекомендованное количество</strong></p><p><img src="'+h+'" alt=""></p><p>Блок содержит опции:</p><p><img src="'+R+'" alt=""></p><ul><li><p><strong>Вычитать из</strong> &quot;<strong>Рекомендуемого количества</strong>&quot; <strong>информацию по</strong> &quot;<strong>Нашим заказам</strong>&quot; <strong>–</strong> при включенной опции, из рекомендуемого для заказа количества будет исключено количество товара, которое уже присутствует в проведенных документах <strong>Наш заказ на склад</strong>.</p></li><li><p><strong>Включать зарезервированный товар в</strong> &quot;<strong>Рекомендуемое количество</strong>&quot; <strong>–</strong> рекомендуемое количество вычисляется от всего наличия товара (свободного и в резерве). Если опция выключена, то рекомендуемое количество товара отталкивается только от свободного наличия товара.</p></li></ul><p><img src="'+r+'" alt=""> <strong>Проценка</strong></p><p><img src="'+b+'" alt=""></p><p>Блок содержит опции, позволяющие указать особенности определения цен на требуемые к заказу позиции:</p><p><img src="'+T+'" alt=""></p><ul><li><p><strong>Валюта</strong> – валюта в которой будут подбираться цены на позиции;</p></li><li><p><strong>Проценивать только товар с МЗ&gt;0</strong> – фильтр позиций с незаполненными в карточке товара или колонке <strong>Автоматический МЗ-ТЗ</strong> минимальным запасом для выбранного склада;</p></li><li><p><strong>Проценивать только товар ≤ ТЗ</strong> – позволяет определить цены только на те позиции, остатки по которым меньше либо равны <strong>Точке заказа</strong> в карточке товара или колонке <strong>Автоматический МЗ-ТЗ</strong>;</p></li><li><p><strong>Производители</strong> – определяет перечень производителей, позиции которых будут процениваться;</p></li><li><p><strong>Искать в: аналогах, синонимах, оригинальных заменах, веб-аналогах</strong> – установите флаги напротив необходимых источников поиска предложений;</p></li><li><p><strong>Поставщики</strong> – определяет перечень поставщиков предложения, от которых (поставщик будет указан в маркетинге) будут использоваться в мастере;</p></li><li><p><strong>Прайс-листы</strong>– при необходимости, укажите перечень прайс-листов, ограничивающий область поиска предложений;</p></li><li><p><strong>Набор фильтров</strong> – позволяет задать набор фильтров для проценки;</p></li><li><p><strong>Приоритет критериев при подборе предложений</strong> – определяет порядок приоритетов при подборе предложений поставщиков: цены закупа, сроков поставки и остатков.</p></li></ul><p>В <strong>Приоритете критериев при подборе предложений</strong> указывается порядок важности критериев цены/срок/наличиепри подборе предложений. Программа каждому предложению поставщика устанавливает вес, учитывая эти критерии. А затем сортирует от лучшего предложения к худшему (по убыванию веса). Алгоритм расчета веса:</p><ul><li><p><strong>Цена</strong>. Разница между минимальной и максимальной ценой разбивается на 10 отрезков. Отрезок с минимальной ценой имеет вес 9, с максимальной – 0. Остальные предложения располагаются в промежутке от 0 до 9 в зависимости от того, в какой отрезок попала цена;</p></li><li><p><strong>Срок</strong>:</p><ul><li><p>0 дней имеет вес 5;</p></li><li><p>1 день имеет вес 4;</p></li><li><p>До 2-7 дней включительно имеет вес 3;</p></li><li><p>До 14 дней включительно имеет вес 2;</p></li><li><p>Более 14 дней либо срок не задан/не распознан имеет вес 1.</p></li></ul></li><li><p><strong>Наличие</strong>:</p><ul><li><p>Наличие у поставщика число и его недостаточно для планируемого количества – вес 0;</p></li><li><p>Наличие у поставщика не задан или задан в нечисловом виде – вес 1;</p></li><li><p>Наличие у поставщика число и его достаточно для планируемого количества – вес 2.</p></li></ul></li></ul><p>Затем программа в соответствии с выбранным порядком важности критериев формирует вес. Например, порядок Цена/Срок/Остаток и цена имеет вес 7, срок 4, остаток 1: значит итоговый вес позиции 741. И так по каждому предложению на товар.</p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>При выборе в критерии <strong>Приоритет критериев при подборе предложений</strong> варианта <strong>Минимальная цена закупа</strong> отбор наилучшего предложения происходит по цене закупа товара. И лишь при равенстве цен нескольких предложений происходит дальнейшее сравнение по сроку и наличию.</p></div><p><img src="'+g+'" alt=""> <strong>Видимость позиций</strong></p><p><img src="'+N+'" alt=""></p><p>Блок содержит опции:</p><p><img src="'+w+'" alt=""></p><ul><li><p><strong>Группировать по группе производителя</strong> – объединяет позиции в группы по производителю детали;</p></li><li><p><strong>Отобразить только позиции с заполненной ценой</strong> – будут скрыты позиции с незаполненной <strong>Ценой закупа</strong>;</p></li><li><p><strong>Отобразить виды групп</strong> – позволяет отрегулировать отображения товаров по группам исходя из:</p><ul><li><p>Нет в наличии, нет в заказах;</p></li><li><p>Нет в наличии, но есть в заказах;</p></li><li><p>Есть в наличии.</p></li></ul></li></ul><p><img src="'+A+'" alt=""> <strong>Анализ продаж</strong></p><p><img src="'+Q+'" alt=""></p><p>Блок <strong>Анализ продаж</strong> служит для настройки анализа объемов продаж, распределенных во времени. На основе заданных настроек формируется два дополнительных блока колонок в позициях мастера: <strong>Продажи</strong> с отдельными колонками по каждому периоду <strong>Временной группировки</strong> анализируемого <strong>Периода</strong>, и <strong>Общие итоги</strong> сколько <strong>Всего продано</strong> товара.</p><p><img src="'+X+'" alt=""></p><p>Рассмотрим каждую настройку для анализа отдельно:</p><ul><li><p><strong>Период</strong> – интервал дат за который будут анализироваться продажи (отгрузки, заказ-наряды) товара. Данные значения сохраняются для каждого пользователя;</p></li><li><p><strong>Наши фирмы</strong> – определяет перечень фирм, по которым будут отбираться документы для анализа;</p></li><li><p><strong>Группы</strong> – группы товара по позициям, которых будет производится анализ;</p></li><li><p><strong>Склады</strong> – склады продажи;</p></li><li><p><strong>МЗ/ТЗ</strong> – фильтрация товаров по значениям минимального запаса/точке заказа. При значении <strong>Все</strong> при запуске анализа будет выполнен автоматический расчет МЗ-ТЗ, если он установлен в настройке <strong>Расчет МЗ-ТЗ</strong>;</p></li><li><p><strong>Временная группировка</strong> – период, по которому будут сгруппированы данные анализа для блока колонок <strong>Продажи</strong>;</p></li><li><p><strong>Итоги</strong> – дополнительная группировка для блока <strong>Общие итоги</strong>.</p></li></ul><p><strong>»</strong> После установки необходимых настроек перейдем к панели инструментов мастера и выполним основные шаги:</p><p><img src="'+E+'" alt=""></p><p><img src="'+t+'" alt=""> <strong>Считать МЗ/ТЗ и остатки</strong></p><p><img src="'+V+'" alt=""></p><p><strong>»</strong> Нажать кнопку <strong>Считать МЗ/ТЗ и остатки</strong> чтобы вычислить значения для блока колонок <strong>Минимальный заказ</strong> – <strong>Точка заказа</strong> по каждой позиции анализируемого склада:</p><p>Колонки <strong>Высокий спрос</strong> и <strong>Низкий спрос</strong> отображают через тире значения МЗ-ТЗ из карточки товара для анализируемого склада.</p><p><img src="'+L+'" alt=""></p><p><img src="'+n+'" alt=""> <strong>Определить цены</strong></p><p><img src="'+P+'" alt=""></p><p><strong>»</strong> Нажмите кнопку <strong>Определить цены</strong> для подбора предложений на позиции. Программа выполнит проценку позиций по прайс-листам от поставщиков в соответствии с установленными настройками в блоке <strong>Проценка</strong> и фильтрами (см. выше) и подберет <strong>Цены закупа</strong> и <strong>Цены продаж</strong>. Предложения по позициям и их аналогам, с учетом видов групп и группировкой по коду/производителю отобразятся в табличной части.</p><p><img src="'+f+'" alt=""></p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Исходные позиции будут выделены жирным шрифтом.</p></div><div class="hint-container warning"><p class="hint-container-title">Внимание!</p><p>При <strong>определении цен</strong> значения <strong>Минимального запаса</strong> и <strong>Точки заказа</strong> будут рассчитаны заново, если выбран автоматический расчет.</p></div><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Позиции, по которым не было подобрано цен или отфильтрованные предложения, не попадут в документ <strong>Наш заказ на склад</strong>.</p></div><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Использование веб-проценки для определения цен на позиции в <strong>Мастере заказов на склад</strong> активируется опцией <strong>Возможность использования веб-проценки в МЗМО</strong> в разделе меню <strong>Управление ► Настройки программы ► Настройки</strong>, группа <strong>Склад и закупки ► Закупки</strong>.Подключение веб-проценки производится через отдел продаж компании &quot;Tradesoft&quot;.</p></div><p><img src="'+o+'" alt=""> <strong>Анализ продаж</strong></p><p><img src="'+C+'" alt=""></p><p><strong>»</strong> При нажатии кнопки <strong>Анализ продаж</strong> табличная часть мастера дополняется секцией <strong>Продажи</strong> и <strong>Общие итоги</strong> с объемами продаж по каждой позиции, как суммарными, так и в динамике за указанные интервалы времени в блоке настроек <strong>Анализ продаж</strong>. На основании этой информации пользователь может принять решение об изменении значения минимального запаса/точки заказа в большую или меньшую сторону, прекратить поддерживать запас какого-либо товара в пользу его аналога и т.д..</p><p><img src="'+r+'" alt=""> <strong>Установка количества</strong></p><p><img src="'+K+'" alt=""></p><p><strong>»</strong> Следующим обязательным шагом в работе мастера является команда <strong>Установка количества</strong>, при выполнении которой заполняется колонка <strong>Рекомендуемое количество</strong> в блоке <strong>Количество товара для заказа</strong>. На данном этапе вычисляется рекомендуемое количество: из минимального запаса вычитается количество товара в наличии, а также вычитается количество товара, который находится в <strong>Заказах поставщику</strong> не под клиентов и в <strong>Наших заказах</strong>. Если получившееся значение меньше нуля, оно сбрасывается в ноль (т.е. данный товар заказывать не требуется). При необходимости, можно произвести корректировку значений по заказываемому количеству.</p><p><img src="'+g+'" alt=""> <strong>Применить (перенос количества)</strong></p><p><img src="'+S+'" alt=""></p><p><strong>»</strong> После установки <strong>Рекомендуемого количества</strong> нажмите кнопку **Применить (перенос кол-ва) ** для переноса значения количества в колонку <strong>Заказать</strong>.</p><p><img src="'+A+'" alt=""> <strong>Заказать</strong></p><p><img src="'+z+'" alt=""></p><p>Команда позволяет создать документ <strong>Наш заказ на склад</strong> для позиций со значением в колонке <strong>Заказать</strong> больше нуля. На основании данного документа в последствии можно сформировать заказы поставщикам.</p><p><img src="'+p+'" alt=""> <strong>Восстановить</strong></p><p><img src="'+M+'" alt=""></p><p>Команда позволяет вернуться к исходному состоянию мастера на момент загрузки в него позиций.</p><p>Рассмотрим колонки табличной части, которые теперь заполнены:</p><ul><li><p>Блок колонок <strong>Наличие</strong>:</p><ul><li><p><strong>Остаток</strong> – текущий остаток на анализируемом складе;</p></li><li><p><strong>Резерв</strong> – количество товара в резерве на анализируемом складе;</p></li><li><p><strong>Остаток на других складах</strong> – остаток товара на других складах, отличных от анализируемого, в том числе недоступных;</p></li><li><p><strong>Общее наличие</strong> – общее наличие товара на всех складах, в том числе недоступных пользователю;</p></li><li><p><strong>Цена продажи</strong> – подобранная цена продажи на позицию;</p></li><li><p><strong>Цена закупа</strong> – подобранная закупочная цена на позицию.</p></li></ul></li></ul><p><img src="'+O+'" alt=""></p><ul><li><p>Блок колонок <strong>Уже заказано на анализируемый склад</strong>:</p><ul><li><p><strong>В Наших заказах</strong> – общее количество товара в проведенных Наших заказах на анализируемый склад;</p></li><li><p><strong>В Заказах поставщикам</strong> – общее количество товара, заказанное у поставщиков на анализируемый склад.</p></li></ul></li></ul><p><img src="'+Z+'" alt=""></p><ul><li><p>Блок колонок <strong>Уже заказано на другие склады</strong>:</p><ul><li><p><strong>В наших заказах на другие склады</strong> – заказанный товар на склады, отличные от анализируемого;</p></li><li><p><strong>В Заказах поставщикам на другие склады</strong> – заказанный товар у поставщиков на склады, отличные от анализируемого.</p></li></ul></li></ul><p><img src="'+G+'" alt=""></p><ul><li><p>Блок колонок <strong>Заказ</strong>:</p><ul><li><p><strong>Поставщик</strong> – наименование поставщика от которого было найдено предложение;</p></li><li><p><strong>Направление (склад)</strong> – направление от поставщика (если известно);</p></li><li><p><strong>Наличие у поставщика</strong> – количество товара в прайс-листе поставщика;</p></li><li><p><strong>Внутренний код поставщика</strong> – внутренний код товара из прайс-листа поставщика;</p></li><li><p><strong>Цена закупа</strong> – закупочная цена от поставщика;</p></li><li><p><strong>Доставка</strong> – срок доставки в днях из маркетинга;</p></li><li><p><strong>Примечания</strong> – поле может быть заполнено вручную;</p></li><li><p><strong>Валюта</strong> – валюта прайс-листа поставщика.</p></li></ul></li></ul><p><img src="'+W+'" alt=""></p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Найденные позиции сортируются в порядке возрастания <strong>Цены закупа</strong>. Позиции без цены попадают в конец списка.</p></div><ul><li><p>Блок колонок <strong>Кол-во товара для заказа</strong>:</p><ul><li><p><strong>Рекомендуемое количество</strong> – количество, которое высчитывается после выполнения шага <strong>Установка кол-ва</strong>;</p></li><li><p><strong>Кратность от поставщика</strong> – коэффициент, согласно которому выбирается количество для заказа от поставщика;</p></li><li><p><strong>Не соответствует кратности от поставщика</strong> – отметка о несоответствии кратности поставщика;</p></li><li><p><strong>Заказать</strong> – количество, которое устанавливается после выполнения шага <strong>Применить (перенос кол-ва)</strong>;</p></li><li><p><strong>Сумма заказа</strong> – сумма исходя из цены закупа и количества в колонке <strong>Заказать</strong>.</p></li></ul></li></ul><p><img src="'+y+'" alt=""></p><ul><li><p>Блок <strong>Общие итоги</strong> содержит колонку <strong>Всего продаж</strong> по позиции за заданный <strong>Период</strong> на всех складах.</p></li><li><p>Блок <strong>Продажи</strong> содержит итоги продаж по товару, сгруппированные в зависимости от заданных настроек <strong>Временная группировка</strong> и <strong>Итоги</strong> в блоке <strong>Анализ продаж</strong>.</p></li></ul><p><img src="'+k+'" alt=""></p>',101)]))}const $=i(D,[["render",q]]),ss=JSON.parse('{"path":"/work/svoe_nalichie/upravlenie_nalichiem/formirovanie_zakaza.html","title":"Формирование заказов с использованием Мастера заказов на склад","lang":"en-US","frontmatter":{"title":"Формирование заказов с использованием Мастера заказов на склад","createTime":"2025/05/14 23:08:05"},"headers":[],"readingTime":{"minutes":8.6,"words":2579},"git":{"updatedTime":1747427491000,"contributors":[{"name":"bONK-cup","username":"bONK-cup","email":"kokosik260302@gmail.com","commits":1,"avatar":"https://avatars.githubusercontent.com/bONK-cup?v=4","url":"https://github.com/bONK-cup"}]},"filePathRelative":"work/svoe_nalichie/upravlenie_nalichiem/formirovanie_zakaza.md","categoryList":[{"id":"67e92c","sort":10000,"name":"work"},{"id":"2fc035","sort":10076,"name":"svoe_nalichie"},{"id":"fa2a37","sort":10109,"name":"upravlenie_nalichiem"}]}');export{$ as comp,ss as data};
