import{_ as s}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.004-D-lAiJXD.js";import{_ as o}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.006-C6TVXSfy.js";import{_ as r}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.008-CELUo195.js";import{_ as n}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.010-DDUYNioq.js";import{_ as g,a as i,b as l,c as p}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.261-CZZx_9yQ.js";import{_ as A}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.017-57kAEd1K.js";import{_ as a}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.019-CQSyaFwC.js";import{_ as e}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.021-QBaRAmgp.js";import{_ as c}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.023-C7RHCIAC.js";import{_ as m,a as d}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.028-Cpi_m1_R.js";import{_ as u}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.030-Cy6WJzwU.js";import{_ as b,c as v,a as f,o as k}from"./app-CUu1n7_5.js";const E="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.268-Bl2jELE2.png",H="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.269-Ftrg8Jqo.png",B="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.270-Di6-FlRa.png",P="/parts-intellect-docs/assets/novyj_zakaz_klienta_1-DMoKslvV.png",h="data:image/png;base64,",D="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.273-CG3UQs1k.png",V="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.274-KZnSJFkX.png",X="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.277-BuDO0_q1.png",L="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.278-DEiyfzGz.png",R="data:image/png;base64,",I="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.280-CRqJyx2o.png",j="data:image/png;base64,",x="data:image/png;base64,",q="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.283-DqDIBlx3.png",Q="data:image/png;base64,",O="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.285-Cxv-ha1f.png",N="data:image/png;base64,",T="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.287-DuuaruoU.png",G={};function z(F,t){return k(),v("div",null,t[0]||(t[0]=[f('<p>Пункт меню позволяет открыть инспектор для создания нового документа <strong>Заказ клиента</strong>:</p><p><img src="'+E+'" alt=""></p><p><img src="'+s+'" alt=""> <strong>Панель действий</strong></p><p><img src="'+H+'" alt=""></p><p>Содержит следующие команды:</p><ul><li><p><strong>Сохранить и закрыть/Сохранить</strong> – позволяет сохранить и закрыть/сохранить документ <strong>Заказ клиенту</strong> без проводки;</p></li><li><p><strong>Сменить состояние</strong> – позволяет осуществить проводку документа или отменить ее, в случае необходимости. А так же добавить документ в архив или извлечь его из архива;</p></li><li><p><strong>Принять оплату</strong> – позволяет отобразить форму оплаты заказа клиента;</p></li></ul><p><img src="'+B+'" alt=""></p><ul><li><p><strong>Создать платеж</strong> – содержит выпадающий список команд для формирования платежных документов:</p><ul><li><p><strong>Приходный кассовый ордер</strong>;</p></li><li><p><strong>Платежное поручение входящее</strong>;</p></li><li><p><strong>Оплата по банковской карте</strong>.</p></li></ul></li><li><p><strong>Создать на основании</strong> – содержит выпадающий список команд позволяющих создать на основании заказа следующие документы:</p><ul><li><p><strong>Задание на отгрузку товара</strong> – позволяет создать документ <strong>Задания на отгрузку товара</strong> для позиций находящихся в состоянии <strong>Резерв</strong> или <strong>Резерв из наличия</strong>;</p></li><li><p><strong>Отказ клиента</strong> – позволяет создать документ <strong>Отказ клиента</strong> для позиций находящихся в состояниях: <strong>Заказ клиента</strong>, <strong>Резерв</strong>, <strong>Заказ поставщику</strong>, <strong>Приход</strong>, <strong>Резерв из наличия</strong>, <strong>Отмена резерва</strong>, <strong>Отказ поставщика</strong>, <strong>Возврат поставщику</strong> и <strong>Кор. прихода</strong>;</p></li><li><p><strong>Заказ поставщику</strong> – позволяет создать документ <strong>Заказ поставщику</strong> для позиций находящихся в состояниях: <strong>Заказ клиента</strong>, <strong>Наш заказ</strong> и <strong>Отказ поставщика</strong>;</p></li><li><p><strong>Заказ клиента (копия с позициями)</strong> – позволяет создать документ <strong>Заказ клиента</strong> с копированием позиций из табличной части <strong>Товары</strong>;</p></li><li><p><strong>Перемещение товаров по местам выдачи заказов</strong> – позволяет создать документ <strong>Перемещения товаров</strong>;</p></li><li><p><strong>Заказ-наряд</strong> – позволяет создать документ <strong>Заказ-наряд</strong> для позиций находящихся в состояниях: <strong>Возврат от клиента</strong>, <strong>Кор. расхода</strong>, <strong>Резерв из наличия</strong>, <strong>Резерв</strong>, <strong>Отмена резерва</strong>, <strong>Приход</strong>, <strong>Заказ клиента</strong> и <strong>Заказ поставщику</strong>;</p></li><li><p><strong>Расходная накладная</strong> – позволяет создать документ <strong>Расходная накладная</strong> для позиций находящихся в состояниях: <strong>Возврат от клиента</strong>, <strong>Кор. расхода</strong>, <strong>На реализации</strong>, <strong>Резерв из наличия</strong>, <strong>Резерв</strong>, <strong>Отмена резерва</strong> и <strong>Приход</strong>;</p></li><li><p><strong>Приходная накладная</strong> – позволяет создать документ <strong>Приходная накладная</strong> для позиций находящихся в состояниях: <strong>Возврат поставщику</strong> и <strong>Заказ поставщику</strong>.</p></li></ul></li><li><p><strong>Экспорт</strong> – команда позволяет экспортировать данные из таблицы в файл.</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Системой поддерживаются следующие форматы выгружаемых файлов:</p><ul><li><p>Файлы *.csv с разделителями &quot;точка с запятой&quot;, &quot;табуляция&quot;.</p></li><li><p>Microsoft Excel 2007 (*.xlsx);</p></li><li><p>Таблица XML 2003 (*.xml);</p></li><li><p>MS Access (*.mdb).</p></li></ul></div><ul><li><p><strong>Связаться</strong> – содержит выпадающий список команд для связи с контрагентами:</p><ul><li><p><strong>Позвонить</strong> – по нажатию на команду выполняется звонок выбранному контрагенту/сотруднику на номер, указанный в карточке этого <strong>Контрагента</strong>/ <strong>Сотрудника</strong>;</p></li><li><p><strong>Открыть чат</strong> – команда позволяет открыть чат с клиентами, подписанными на Telegram-бота;</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Для работы сервиса отправки уведомлений и сообщений через Telegram-бот необходимо:▪ подключение дополнительной услуги. За подключением услуги обратитесь в отдел продаж Компании Tradesoft;▪ созданный и настроенный Telegram-бот;▪ установленный и подключенный <strong>Сервер Parts.Intellect</strong>.Подробнее о работе модуля читайте в <a href="https://product-doc.tradesoft.ru/ai/telegram/index.htm" target="_blank" rel="noopener noreferrer">руководстве пользователя</a></p></div><ul><li><strong>Уведомить клиента</strong> – позволяет отправить клиенту сообщение по настроенному событию <strong>Заказ клиента готов к выдаче</strong>;</li></ul></li><li><p><strong>Печать</strong> – позволяет отобразить окно <strong>Мастер печати</strong>:</p></li></ul><p><img src="'+P+'" alt=""></p><ul><li><p><strong>Прочее</strong> – содержит выпадающий список с командой <strong>Навигация по связям</strong>, которая позволят отобразить панель с информацией по связанным документам для текущего документа;</p></li><li><p><strong>Помощь</strong> – позволяет открыть руководство пользователя на странице <strong>Создание нового заказа клиента</strong>.</p></li></ul><p><img src="'+o+'" alt=""> <strong>Задачи</strong></p><p><img src="'+h+'" alt=""></p><p>Блок содержит информацию о <strong>Контрагенте</strong>:</p><ul><li><p><strong>Контрагент</strong> – отображает следующую информацию:</p><ul><li><p>наименование контрагента;</p></li><li><p><strong>Баланс по заказам</strong>;</p></li><li><p><strong>Баланс фактический</strong>;</p></li><li><p><strong>Баланс просроченный</strong>;</p></li><li><p><strong>Баланс по возвратам</strong>;</p></li><li><p><strong>Код контрагента</strong> – выводит уникальный номер контрагента;</p></li><li><p><strong>Категория клиента</strong>;</p></li></ul></li><li><p><strong>Блокировка</strong> – статус блокировки контрагента;</p></li><li><p><strong>Контакты</strong> – контактные данные контрагента.</p></li></ul><p><img src="'+r+'" alt=""> <strong>Основные параметры</strong></p><p><img src="'+D+'" alt=""></p><p>Позволяет задать параметры документа:</p><ul><li><p><strong>Дата –</strong> дата и время создания <strong>Заказ клиента</strong>;</p></li><li><p><strong>Номер –</strong> номер документа. Возможен как ручной ввод, так и автозаполнение;</p></li><li><p><strong>Внешний номер –</strong> внешний номер документа;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Для пользователя может быть разрешено изменения поля <strong>Внешний номер</strong> даже после проводки документа. Разрешение регулируется опцией <strong>Можно изменять внешний номер документа после проводки</strong> в разделе меню <strong>Управление ► Настройки программы ► Роли пользователей</strong> <strong>► Документы ► Заказы клиентов</strong></p></div><ul><li><strong>Место выдачи –</strong> торговая точка, с которой предполагается выдавать товар клиенту. Данное место выдачи используется при отправке <strong>Уведомления о готовности заказа</strong>, а так же для создания документа <strong>Перемещение</strong> на основании <strong>Приходной накладной</strong>, который позволяет автоматически распределить товар на места выдачи;</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>При активной опции <strong>Учитывать место выдачи товара</strong> в <strong>Заказе клиента</strong> в разделе меню <strong>Управление ►</strong> <strong>Настройки программы ► Настройки,</strong> группа <strong>Уведомления</strong> уведомление о готовности заказа клиента к выдаче будет уходить только в случае, если товар был оприходован либо перемещен на склад/торговую точку, которая указана в качестве <strong>Место выдачи</strong> товара в параметрах <strong>Заказ клиента</strong>.</p></div><ul><li><p><strong>Наша фирма –</strong> наша фирма на которую оформляется заказ;</p></li><li><p><strong>Скидка, %/Наценка, %</strong> (значение меняется при нажатии на имя поля) – значение процента скидки или наценки, которое применяется на все позиции документа. Значение можно указывать в процентах в поле <strong>Скидка (расчетная), %</strong>, или указать непосредственно <strong>Сумму скидки</strong>. Значения полей рассчитываются автоматически на основании значения второго поля;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Доступ к полю определяется разрешением пользователя <strong>Можно изменять скидку/наценку</strong> в разделе меню <strong>Управление ► Настройки программы ► Роли пользователей ► Документы ► Заказы клиентов</strong>.</p></div><p><img src="'+V+'" alt=""></p><ul><li><p><strong>Тип оплаты –</strong> способ внесения предоплаты по документу из словаря <strong>Способы оплаты</strong>;</p></li><li><p><strong>Минимальная предоплата –</strong> сумма предоплаты за заказ. Рассчитывается автоматически на основании процента, заданного в настройке <strong>Минимальный % предоплаты по ЗК</strong>, в разделе меню <strong>Управление ► Настройки программы ► Настройки</strong>, группа <strong>CRM ► Контрагенты</strong> – для всех клиентов или с помощью аналогичной настройки в карточке клиента в разделе <strong>CRM</strong> <strong>► Клиенты</strong>, вкладка <strong>Финансы</strong>. Настройка в карточке клиента имеет наивысший приоритет. Полученная сумма предоплаты округляется до целого в большую сторону, редактирование суммы вручную недоступно;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Для применения процента необходимо также активировать опцию <strong>Запретить проводить ЗК без минимальной предоплаты</strong> (раздел <strong>Управление ► Настройки программы ► Роли пользователей ► Документы ► Заказы клиентов</strong>). При активном значении настройки проведение документа <strong>Заказ клиента</strong> без внесения предоплаты будет недоступно.</p><p>При наличии суммы <strong>Минимальная предоплата</strong> во время проведения заказа будет открыт интерфейс оплаты, в поле ввода автоматически проставится сумма, равная <strong>Минимальная предоплата</strong> (внесение суммы меньше недоступно). После принятия оплаты будет создан платежный документ и долг по документу сократится на сумму предоплаты. Отобразить интерфейс оплаты без использования предоплаты можно с помощью опции <strong>Предлагать оплатить документ при проводке</strong> в разделе <strong>Управление ► Настройки программы ► Роли пользователей ► Документы ► Заказы клиентов</strong>. Способы оплаты определяются настройкой <strong>Доступные типы оплат</strong> в том же разделе.</p></div><ul><li><strong>Клиент –</strong> клиент, который заказывает товар. Клиента можно выбрать из справочника контрагентов, открыть форму для создания нового или ввести данные клиента для поиска по справочнику. Поиск ведется по наименованию, коду, номеру дисконтной карты, номерам телефона и мобильного телефона, электронной почте, номеру ИНН, а также системному штрихкоду клиента. При вводе текста предлагается список клиентов из справочника, у которых встречается введенная последовательность символов. Если был выбран существующий контрагент из справочника, то программа привяжет его к документу.</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Если в карточке контрагента заполнены поля <strong>Способ доставки по умолчанию</strong>, <strong>Адрес доставки</strong>, а на вкладке <strong>Автомобили</strong> есть добавленные автомобили, то при выборе этого клиента в документе <strong>Заказ клиента</strong>:</p><ul><li><p>в поле <strong>Автомобиль</strong> появится выпадающий список с возможностью выбрать автомобиль клиента;</p></li><li><p>в поле <strong>Способ доставки</strong> подставится значение из карточки;</p></li><li><p>в поле <strong>Адрес доставки</strong> подставится значение из карточки.</p></li></ul></div><ul><li><p><strong>Автомобиль –</strong> автомобиль клиента. В поле отображается: марка и модель автомобиля, VIN-код, год выпуска, объем, типа и номер двигателя (при условии, что данная информация внесена в справочник <strong>Автомобили</strong>);</p></li><li><p><strong>Способ доставки –</strong> способ доставки товара;</p></li><li><p><strong>Адрес доставки –</strong> адрес клиента для доставки;</p></li><li><p><strong>Плановая дата исполнения –</strong> значение рассчитывается автоматически на основании значения самой поздней <strong>Плановая дата доставки</strong> среди позиций документа. Подразумевается, что весь заказ может быть полностью исполнен, когда будет доставлена последняя деталь из заказа;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Значение поля <strong>Плановая дата исполнения</strong> не может быть меньше, чем значение поля <strong>Дата</strong>.</p></div><ul><li><p><strong>Статус заказа –</strong> значение устанавливается программой. Для вычисления статуса используются данные о состоянии позиций, проводке, плановой дате исполнения и дате готовности к выдаче. Поле <strong>Статус заказа</strong> принимает следующие значения:</p><ul><li><p><strong>Новый</strong> – документ <strong>Заказ клиента</strong> не проведен;</p></li><li><p><strong>В работе</strong> – позиции документа <strong>Заказ клиента</strong> не готовы к выдаче и требуют внимания менеджера;</p></li><li><p><strong>В работе, просрочен</strong> – позиции документа <strong>Заказ клиента</strong> не готовы к выдаче, требуют внимания менеджера, а значение поля <strong>Плановая дата исполнения</strong> раньше текущей даты;</p></li><li><p><strong>Ожидает выдачи, n дней</strong> – позиции документа <strong>Заказ клиента</strong> готовы к выдаче, а значение поля <strong>Дата готовности к выдаче</strong> раньше текущей даты на <strong>n</strong> дней;</p></li><li><p><strong>Исполнен</strong> – все позиции документа <strong>Заказ клиента</strong> достигли конечного состояния: <strong>Возврат от клиента</strong>, <strong>Отказ клиента</strong>, <strong>Расходная накладная</strong>, <strong>Заказ-наряд</strong>, <strong>Корректировка расходной накладной</strong>;</p></li></ul></li><li><p><strong>Номер счёта на оплату –</strong> если есть необходимость формирования счета на оплату для клиента, нажмите на кнопку ![ref34] для присвоения номера новому счету (распечатать счет можно через команду <strong>Печать</strong>).</p></li><li><p><strong>Дата счёта на оплату –</strong> устанавливается автоматически, для изменения даты нажмите на кнопку ![ref35] и выберите нужную дату с помощью календаря;</p></li><li><p><strong>Комментарий для доставки –</strong> поле для ввода комментария.</p></li></ul><p><img src="'+n+'" alt=""> <strong>Дополнительные параметры</strong></p><p><img src="'+X+'" alt=""></p><p>Блок содержит поля:</p><ul><li><strong>Валюта</strong> – валюта цен в документе. Если изменить валюту документа программа предложит сконвертировать цены на позиции к валюте документа по текущему курсу для валют;</li></ul><p><img src="'+L+'" alt=""></p><ul><li><p><strong>Отметка</strong> – отметка для документа из справочника;</p></li><li><p><strong>Стоимость доставки</strong> – влияние стоимости доставки на цену закупа:</p><ul><li><p><strong>Не включать стоимость доставки</strong> – суммарное значение по колонке <strong>Стоимость доставки</strong> не будет влиять на общую сумму документа;</p></li><li><p><strong>Включать стоимость без учета скидки</strong> – сумма колонки <strong>Стоимость доставки</strong> будет прибавлена к сумме по документу;</p></li><li><p><strong>Включать стоимость с учетом скидки</strong> – на стоимость доставки будет действовать <strong>Скидка, %/Наценка, %</strong> по документу.</p></li></ul></li><li><p><strong>Источник обращения</strong> – источник добавления документа в журнал. Возможные источники и отметки:</p><ul><li><p><img src="'+g+'" alt=""> – документ был передан с сайта Parts.Resource при тике синхронизации;</p></li><li><p><img src="'+i+'" alt=""> – документ был создан из окна звонка. Доступно при наличии подключенной IP-телефонии;</p></li><li><p><img src="'+l+'" alt=""> – источник, который можно присвоить документам вручную;</p></li><li><p><img src="'+p+'" alt=""> – документ загружен с почты с использованием правила из раздела <strong>CRM ► Автоимпорт заказов клиентов</strong>.</p></li></ul></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Источники <strong>Сайт</strong>, <strong>Звонок</strong>, <strong>Email</strong> присваиваются документам автоматически. При необходимости источник можно изменить вручную. Отредактировать источники и цвета отметок можно в разделе <strong>Управление</strong> ► <strong>Справочники</strong> ► <strong>Источники обращений</strong>.</p></div><ul><li><strong>Выделять НДС</strong> – определяет наличие ставки НДС при расчете стоимости позиций, по умолчанию значение берется из карточки <strong>Нашей фирмы</strong>. При необходимости значение можно изменить.</li></ul><p><img src="'+A+'" alt=""> <strong>Товары</strong></p><p>Панель действий блока содержит следующие команды:</p><ul><li><p><strong>Добавить из проценки</strong> – позволяет открыть инструмент проценки, для поиска и добавления в документ необходимых позиций товаров.</p></li><li><p><strong>Добавить</strong> – позволяет добавить товары в заказ**.** Доступны следующие источники позиций:</p><ul><li><p><strong>Из справочника</strong> – позиция будет добавлена в документ без цен из справочника <strong>Товары</strong>;</p></li><li><p><strong>Из справочника через проценку –</strong> позицию можно будет выбрать вначале в справочнике товаров, а затем она будет передана в <strong>Проценку</strong> для подбора предложений;</p></li><li><p><strong>Из корзины</strong> – позиции будут добавлены из корзины;</p></li><li><p><strong>Копия позиции</strong> – будет создана копия выделенной позиции документа.</p></li></ul></li><li><p><strong>Удалить</strong> – позволяет удалить позицию из табличной части;</p></li><li><p><strong>Действия с позицией</strong> – позволяет произвести действия с позицией:</p><ul><li><p><strong>Добавить в корзину</strong> – добавляет выбранные в табличной части товары в корзину пользователя;</p></li><li><p><strong>Создать новые товары</strong> – позволяет добавить выделенные товары в справочник <strong>Товары</strong>, если ранее карточка товара не была создана;</p></li><li><p><strong>Приоритетный склад</strong> – позволяет указать <strong>Приоритетный склад списания</strong> из справочника <strong>Склады/Торговые точки</strong>. Указать приоритетный склад для списания можно так же в самой позиции документа, в колонке <strong>Приоритетный склад для списания</strong>.</p></li></ul></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>По умолчанию списание товара (в момент проводки) будет осуществляться среди всех складов доступных для списания пользователю, по действующему в системе <strong>Способу списания товара ФИФО/ЛИФО</strong> в разделе меню <strong>Управление ► Настройки программы ► Настройки ►</strong> группа <strong>Склад и закупки ► Движение товара.</strong></p></div><ul><li><p><strong>НДС</strong> – содержит следующие команды:</p><ul><li><p><strong>Изменить ставку НДС</strong> – позволяет изменить ставку НДС для выбранных позиций;</p></li><li><p><strong>Установить приоритет цен с НДС/Снять приоритет цен с НДС –</strong> позволяет установить/снять приоритет цен с НДС, который определяет ключевую цену, на основании которой будет высчитываться вторая цена при переключении в параметрах документа значения <strong>Выделять НДС</strong>:</p><ul><li><p>если для позиции активирована опция <strong>Установить приоритет цен с НДС</strong>, то при включении переключателя <strong>Выделять НДС</strong> значение в колонке <strong>Цена с НДС</strong> не изменится, а <strong>Цена без НДС</strong> рассчитается на ее основании;</p></li><li><p>убрать приоритет цен можно с помощью опции <strong>Снять приоритет цен с НДС</strong>, если приоритет снят, то при включении переключателя <strong>Выделять НДС</strong> значение в колонке <strong>Цена без НДС</strong> не изменится, а <strong>Цена с НДС</strong> рассчитается на ее основании.</p></li></ul></li></ul></li><li><p><strong>Заполнить маркетинг</strong> – позволяет автоматически подставить информацию о позиции из существующих маркетингов. Доступна подстановка:</p><ul><li><p><strong>Закупочной цены</strong> <strong>(с НДС)</strong> – цена закупа детали у поставщика;</p></li><li><p><strong>Цены продажи (с НДС)</strong> – цена на товар с учетом маркетинга;</p></li><li><p><strong>Направления/ склада</strong> – направление поставки или склад из прайс-листа;</p></li><li><p><strong>Приоритетного склада для списания</strong> – склад, с которого требуется осуществлять списание товара;</p></li></ul></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Заполнить маркетинг можно как для одной позиции из документа, нажав кнопку <strong>Обновить</strong>, так и для всех, нажав кнопку <strong>Для всех</strong> в открывшемся окне.Функция <strong>Заполнить маркетинг</strong> в <strong>Заказах клиентов</strong> доступна только для позиций с пустым полем <strong>Маркетинг</strong> из источников: <strong>Прайс-листов наличия</strong> и <strong>Прайс-лист поставщика</strong>. Функция активна только для не проведенных документов.Для подстановки цен по позиции выполняется проценка с поиском позиции в источниках (из маркетинга, под заказ, из наличия, в пути). Проценка осуществляется в 3 этапа, поиск позиции осуществляется по точному совпадению, поиск по аналогам и синонимам не осуществляется:</p><ul><li><p><strong>1 этап</strong>:</p><ul><li><p>Проверка источника на доступность (в соответствии с разрешениями пользователя в разделе <strong>Управление</strong> <strong>► Настройки программы ► Разрешения для роли пользователей ► Прайс-листы</strong>;</p></li><li><p>Получение товара из прайс-листа;</p></li><li><p>Загрузка списков маркетингов;</p></li><li><p>Разбитие позиции по складам;</p></li><li><p>Выбор подходящего маркетинга (проверка по диапазону цен, группе скидок, производителю, группе товаров и т.д.);</p></li><li><p>Для товаров в наличии: исключение позиций с неподходящим складом/ Торговой точкой, на которой расположен товар (в соответствии с выбранным Складом/ Торговой точкой в документе);</p></li><li><p>Исключение маркетингов, не подходящих по категории клиента;</p></li><li><p>Для каждого подходящего маркетинга высчитывается цена;</p></li><li><p>Выбор одного маркетинга по весам.</p></li></ul></li><li><p><strong>2 этап</strong>:</p><ul><li>Удаление дубликатов из списка процененных позиций товара;</li></ul></li><li><p><strong>3 этап</strong>:</p><ul><li>Возврат списка процененных позиций из разных источников. В качестве результата берется первый из списка товар.</li></ul></li></ul><p>Для партионной СЦО выполняется сортировка по дате из <strong>Приходной накладной</strong>. Выбор позиции товара из проценки зависит от настройки <strong>Способ списание товара</strong> (<strong>ЛИФО</strong>, <strong>ФИФО</strong>), который задается в разделе <strong>Управление ► Настройки программы ► Настройки ►</strong> группа <strong>Склад и закупки ► Движение товара</strong>. Если в списке присутствуют только позиции из <strong>Прайс-листов поставщиков</strong>, по которым нет <strong>Приходных накладных</strong> – сортировка не выполняется.</p></div><ul><li><p><strong>Изменить наценку позиций</strong> – позволяет установить наценку, либо скидку на выделенные позиции. На форме указывается от какой цены необходимо начислять наценку или скидку, а также размер скидки/наценки: фиксированный; из карточки товара. Далее с помощью опции <strong>Применить правила округления валюты</strong> можно округлить полученное значение.</p></li><li><p><strong>Прочее</strong> – содержит выпадающий список команд:</p><ul><li><p><strong>Показать/скрыть изображения</strong> – позволяет открыть окно <strong>Изображения</strong>, в котором содержатся изображения выбранного товара;</p></li><li><p><strong>Информация</strong> – позволяет отобразить информацию о предыдущих документах и позициях выбранного товара.</p></li></ul></li></ul><p>Табличная часть <strong>Товары</strong> содержит список добавленных позиций в документ <strong>Заказ клиента</strong>.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Список, порядок и отображение столбцов в <strong>Табличной части</strong> настраивается индивидуально каждым пользователем программы.Для отображения/скрытия колонок используется команда <strong>Выбор колонок</strong>, которая открывает одноименное окно.</p></div><p>Доступные колонки табличной части <strong>Товары</strong>:</p><ul><li><p><strong>Базовая валюта –</strong> валюта, в которой были указаны цены, на момент добавления позиции в документ;</p></li><li><p><strong>Базовая закупочная цена –</strong> цена закупа из &quot;источника&quot; добавления на момент добавления позиции в документ (обычно цена от поставщика);</p></li><li><p><strong>Базовая цена продажи –</strong> цена продажи из &quot;источника&quot; позиции на момент добавления позиции в документ;</p></li><li><p><strong>В наличии на доступных складах</strong> – общее наличие по товару на всех складах, доступных пользователю для работы;</p></li><li><p><strong>В резерве на доступных складах</strong> – общее количество зарезервированного товара на всех складах, доступных пользователю для работы;</p></li><li><p><strong>Валюта</strong> –** валюта товара подставляемая на основании <strong>Валюта</strong> документа;</p></li><li><p><strong>Веб-Инфо</strong> – колонка содержит команду вызова окна, содержащего общую информацию и изображения детали из сервиса веб-инфо (по вопросу подключения сервиса можно уточнить информацию в отделе продаж);</p></li><li><p><strong>Внутренний код</strong> – <strong>Внутренний код</strong> из карточки товара;</p></li><li><p><strong>Внутренний код поставщика</strong> – <strong>Внутренний код</strong> товара из прайс-листа поставщика;</p></li><li><p><strong>Внутренний резерв –</strong> отображает время до окончания <strong>Внутреннего резерва</strong> по позиции (<strong>Управление ► Настройки программы ► Настройки ►</strong> группа <strong>Склад и закупки ► Движение товара</strong>);</p></li><li><p><strong>Возможность возврата</strong> – отображает признак возможности возврата из прайс-листа;</p></li><li><p><strong>Время доставки –</strong> время доставки товара;</p></li><li><p><strong>Группа товара</strong> – основная группа товара из карточки;</p></li><li><p><strong>Дополнительные свойства товара</strong> – дополнительные свойств товара из карточки;</p></li><li><p><strong>Единица измерения</strong> – указывает <strong>Единица измерения</strong> из карточки товара;</p></li><li><p><strong>Закупочная цена (без НДС)</strong> – цена закупа детали у поставщика** без учета НДС;</p></li><li><p><strong>Закупочная цена (с НДС)</strong> – цена закупа детали у поставщика с учетом значения колонки <strong>Ставка НДС, %</strong>;</p></li><li><p><strong>Закупочная цена со скидкой и доставкой (с НДС)</strong> – цена закупа с учетом процента скидки/наценки по документу и НДС;</p></li><li><p><strong>Закупочная цена со скидкой и доставкой (без НДС)</strong> – цена закупа с учетом процента скидки/наценки по документу и без учета НДС** ;</p></li><li><p><strong>Замены товара</strong> – поле заполняется информацией об исходном замененном коде товара в документе (например, если в заказе поставщику был отличный артикул);</p></li><li><p><strong>Идентификатор в Parts.Resource –</strong> краткий символьный идентификатор** товара на сайте Parts.Resource;</p></li><li><p><strong>Источник позиции –</strong> источник позиции товара;</p></li><li><p><strong>Итоговая стоимость (без НДС)</strong> – стоимость товара с учетом процента скидки/наценки по документу и значения колонки <strong>Стоимость доставки</strong> (если включен ее учет);</p></li><li><p><strong>Итоговая стоимость (с НДС)</strong> – стоимость товара с учетом процента скидки/наценки по документу, значения колонок <strong>Стоимость доставки</strong> (если включен ее учет) и <strong>Ставка НДС,</strong> % (если выделяется НДС);</p></li><li><p><strong>Категория клиента из маркетинга</strong> – категория клиента из <strong>Маркетинг</strong> (если к позиции применен маркетинг)** ;</p></li><li><p><strong>Код</strong> – артикул детали;</p></li><li><p><strong>Кол-во</strong> – количество товара;</p></li><li><p><strong>Количество по доставке</strong> – количество товаров, партий и т.п. для расчета <strong>Стоимости доставки</strong>;</p></li><li><p><strong>Кратность наличия</strong> – коэффициент, согласно которому может быть выбрано количество для заказа из наличия. Также выполняет роль минимального количества к заказу;</p></li><li><p><strong>Кратность от поставщика –</strong> коэффициент, согласно которому может быть выбрано количество для заказа от поставщика. Также выполняет роль минимального количества к заказу;</p></li><li><p><strong>Маркетинг</strong> – информация о <strong>Маркетинге</strong>, который применился к позиции при добавлении из <strong>Проценки</strong> или <strong>Корзины</strong>;</p></li><li><p><strong>Меньше минимальной партии от поставщика –</strong> указывает признак если <strong>Кол-во</strong> товара меньше, чем <strong>Минимальная партия от поставщика</strong>;</p></li><li><p><strong>Минимальная партия от поставщика –</strong> минимальное количество единиц товара под заказ в одной партии;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Чтобы настроить разрешение на проведение документа с количеством меньше минимальной партии, в разделе <strong>Управление ► Настройки программы</strong>, вкладка <strong>Роли пользователей ► Разрешения для роли пользователей,</strong> группы настроек <strong>Заказы клиентов, Наши заказы на склад, Заказы поставщикам, VIN-запросы</strong> воспользуйтесь настройкой <strong>Можно проводить с количеством меньше минимальной партии поставщика</strong> для нужных документов и пользователей.</p></div><ul><li><p><strong>Направление/Склад</strong> – направление поставки или склад из прайс-листа;</p></li><li><p><strong>Наценка, %</strong> – наценка на товары в отношении закупочной цены к цене продажи по документу;</p></li><li><p><strong>Не соответствует кратности наличия</strong> – отметка о несоответствии кратности из наличия товара;</p></li><li><p><strong>Не соответствует кратности от поставщика –</strong> отметка о несоответствии кратности товара от поставщика;</p></li><li><p><strong>Плановая дата доставки –</strong> отображает дату доставки детали;</p></li><li><p><strong>Плановая цена –</strong> отображает ориентировочную версию итоговой цены товара;</p></li><li><p><strong>Плановое количество –</strong> отображает ориентировочную версию итогового количества товара;</p></li><li><p><strong>Подакцизный товар</strong> – указывает признак <strong>Подакцизный товар</strong> из карточки товара;</p></li><li><p><strong>Последняя цена продажи из РН</strong> – цена продажи из последней <strong>Расходной накладной</strong> по товару;</p></li><li><p><strong>Поставщик из маркетинга –</strong> отображает поставщика указанного в примененном к позиции маркетинге;</p></li><li><p><strong>Примечания</strong> – примечание к позиции;</p></li><li><p><strong>Приоритет цен с НДС</strong> – позволяет установить/снять приоритет цен с НДС, который определяет ключевую цену, на основании которой будет высчитываться вторая цена при переключении в параметрах документа значения <strong>Выделять НДС</strong>;</p></li><li><p><strong>Приоритетный склад для списания</strong> – склад, с которого требуется осуществлять списание товара;</p></li><li><p><strong>Референс от клиента –</strong> содержит информацию по товару от клиента;</p></li><li><p><strong>Свойства от клиента –</strong> содержит информацию по свойствам товара от клиента;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>В полях <strong>Референс от клиента</strong> и <strong>Свойства от клиента</strong> содержится информация от клиента для идентификации позиции, например,** информацию по которой понимает по какому прайс-листу поставщика/договору оформляется заказ.</p></div><ul><li><p><strong>Системный штрихкод</strong> – штрихкод позиции документа, присваиваемый системой;</p></li><li><p><strong>Состояние позиции</strong> – состояние (и подсостояние) позиции;</p></li><li><p><strong>Ставка НДС, %</strong> – значение ставки в процентах;</p></li><li><p><strong>Статус</strong> – статус товара;</p></li><li><p><strong>Стоимость без скидки (с НДС)</strong> – стоимость товара без скидки с учетом НДС;</p></li><li><p><strong>Стоимость доставки</strong> – сумма по доставке товара (<strong>Цена доставки</strong> * <strong>Количество по доставке</strong>);</p></li><li><p><strong>Сумма НДС</strong> – сумма НДС по позиции с учетом процента скидки/наценки;</p></li><li><p><strong>Товар</strong> – наименование детали (из источника позиции);</p></li><li><p><strong>Товар. Альтернативное название</strong> – <strong>Альт. наименование</strong> указанное в карточке товара;</p></li><li><p><strong>Товар. Название –</strong> наименование детали из карточки товара;</p></li><li><p><strong>Товар. Производитель</strong> – <strong>Производитель</strong> товара из карточки товара;</p></li><li><p><strong>Товар. Стандартная наценка</strong> – <strong>з</strong>начение наценки и карточки товара;</p></li><li><p><strong>Цена доставки</strong> – значение из справочника <strong>Стоимости доставок</strong> ;</p></li><li><p><strong>Цена закупа в прайс-листе из наличия –</strong> значение <strong>Цена закупа</strong> в <strong>Прайс-листе из наличия</strong> (только для <strong>Расширенной</strong> схемы ценообразования);</p></li><li><p><strong>Цена продажи (без НДС)</strong> – цена продажи, если она присутствовала в &quot;источнике&quot; позиции** или была введена вручную;</p></li><li><p><strong>Цена продажи (с НДС)</strong> – цена продажи, если она присутствовала в &quot;источнике&quot; позиции или была введена вручную, с учетом процента скидки/наценки по документу (если выделяется НДС);</p></li><li><p><strong>Цена продажи со скидкой и доставкой (без НДС)</strong> – цена продажи, если она присутствовала в &quot;источнике&quot; позиции** или была введена вручную, с учетом процента скидки/наценки по документу, значения колонок <strong>Стоимости доставки</strong> (если включен ее учет) и <strong>Ставка НДС,</strong> <strong>%</strong> (если выделяется НДС);</p></li><li><p><strong>Цена продажи со скидкой и доставкой (c НДС)</strong> – цена продажи, если она присутствовала в &quot;источнике&quot; позиции или была введена вручную, с учетом процента скидки/наценки по документу, значения колонки <strong>Стоимости доставки</strong> (если включен ее учет);</p></li><li><p><strong>Штрихкод внутренний</strong> –** внутренний штрихкод из карточки товара.</p></li></ul><p><img src="'+a+'" alt=""> <strong>Найти/Добавить</strong></p><p><img src="'+R+'" alt=""></p><p>Поле предназначено для поиска товара или добавления найденной позиции через проценку по коду детали.</p><p><img src="'+e+'" alt=""> <strong>Строка информации</strong></p><p><img src="'+I+'" alt=""></p><p>Отображает информацию по позициям в документе:</p><ul><li><p><strong>Всего товаров</strong> – количество товаров добавленных в табличную часть;</p></li><li><p><strong>Сумма итого</strong> – суммарная стоимость товаров добавленных в табличную часть;</p></li><li><p><strong>Сумма НДС</strong> – суммарное значение НДС по всем позициям товаров в табличной части;</p></li><li><p><strong>Сумма доставки</strong> – суммарная стоимость доставки для всех товаров;</p></li><li><p><strong>Прибыль по заказу</strong> – суммарная прибыль по всем позициям товаров в табличной части;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>По умолчанию поле <strong>Прибыль по заказу</strong> не отображено в информационной строке. Для его отображения необходимо включить видимость в разделе <strong>Управление ► Настройки программы ► Роли пользователей ► Разрешения для роли пользователей ► Документы ► Заказы клиентов</strong> в группе <strong>Видимость</strong>.</p></div><ul><li><strong>Долг по заказу</strong> – общая задолженность по товарам добавленным в табличную часть.</li></ul><p><img src="'+c+'" alt=""> <strong>Общее</strong></p><p><img src="'+j+'" alt=""></p><p>Блок содержит поля:</p><ul><li><p><strong>Комментарий</strong> – позволяет оставить комментарий к заказу клиента;</p></li><li><p><strong>Комментарий клиента Parts.Resource</strong> – содержит комментарий клиента к заказу, полученный с сайта Parts.Resource.</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Поле <strong>Комментарий клиента Parts.Resource</strong> доступно при подключенной услуге Синхронизатор. Подробнее читайте в <a href="https://product-doc.tradesoft.ru/ai/synch/index.htm" target="_blank" rel="noopener noreferrer">руководстве пользователя</a>.</p></div><p><img src="'+m+'" alt=""> <strong>Список платежей</strong></p><p><img src="'+x+'" alt=""></p><p>Список содержит информацию о платежах по данному заказу.</p><p><img src="'+q+'" alt=""></p><p><img src="'+d+'" alt=""> <strong>Журнал действий</strong></p><p><img src="'+Q+'" alt=""></p><p>Данный журнал отражает действия всех пользователей с документом.</p><p><img src="'+O+'" alt=""></p><p><img src="'+u+'" alt=""> <strong>Журнал уведомлений</strong></p><p><img src="'+N+'" alt=""></p><p>Журнал содержит информацию о работе системы уведомлений клиентов и менеджеров, через Email, SMS и Telegram по документу. В журнал выводятся все события по отправке уведомлений, как успешные, так и нет.</p><p><img src="'+T+'" alt=""></p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Отправка уведомлений через приложение Telegram доступно только для пользователей с подключенным модулем.</p></div>',83)]))}const $=b(G,[["render",z]]),tt=JSON.parse('{"path":"/specification/crm/novyj_zakaz_klienta.html","title":"Новый Заказ клиента","lang":"en-US","frontmatter":{"title":"Новый Заказ клиента","createTime":"2025/04/21 21:02:16"},"headers":[],"readingTime":{"minutes":11.88,"words":3565},"git":{"updatedTime":1750007824000,"contributors":[{"name":"a.lutoshkin","username":"","email":"a.lutoshkin@tradesoft.ru","commits":2,"avatar":"https://gravatar.com/avatar/605ee9d365e3a6380f8e9c71f9269ee5373d537aebefbece4e023cac396dfc8d?d=retro"}]},"filePathRelative":"specification/crm/novyj_zakaz_klienta.md","categoryList":[{"id":"fd8b54","sort":10022,"name":"specification"},{"id":"2ac25b","sort":10064,"name":"crm"}]}');export{$ as comp,tt as data};
