import{_ as s}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.004-D-lAiJXD.js";import{_ as o}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.149-QTCn8Zgq.js";import{_ as r}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.006-C6TVXSfy.js";import{_ as n}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.008-CELUo195.js";import{_ as g}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.010-DDUYNioq.js";import{_ as i}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.017-57kAEd1K.js";import{_ as p}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.019-CQSyaFwC.js";import{_ as l}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.021-QBaRAmgp.js";import{_ as A}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.023-C7RHCIAC.js";import{_ as a,a as e}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.028-Cpi_m1_R.js";import{_ as c}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.030-Cy6WJzwU.js";import{_ as m}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.033-D067HoDi.js";import{_ as u,c as d,a as b,o as v}from"./app-CUu1n7_5.js";const h="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.640-mqQwUDf7.png",C="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.641-C0AQZ_8s.png",B="/parts-intellect-docs/assets/novyj_zakaz_postavshchiku_1-BheiP4fu.png",U="data:image/png;base64,",G="data:image/png;base64,",V="data:image/png;base64,",S="data:image/png;base64,",q="data:image/png;base64,",f="data:image/png;base64,",X="data:image/png;base64,",w="data:image/png;base64,",I="data:image/png;base64,",M="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.652-K0e9sdy8.png",k="data:image/png;base64,",E="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.654-Ll-R20ZK.png",K="data:image/png;base64,",O="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.656-CpH2daXD.png",y={};function D(T,t){return v(),d("div",null,t[0]||(t[0]=[b('<p>Данный пункт позволяет открыть инспектор для создания документа <strong>Заказ поставщику</strong>.</p><p><img src="'+h+'" alt=""></p><p><img src="'+s+'" alt=""> <strong>Панель инструментов</strong></p><p><img src="'+C+'" alt=""></p><p><strong>Панель инструментов</strong> позволяет осуществить над выбранными позициями документа типа <strong>Заказ поставщику</strong> ряд <a href="#370df7fa-a8cb-49ea-a363-e5760b2f112f">действий</a>:</p><ul><li><p><strong>Сохранить и закрыть</strong> – позволяет сохранить и закрыть документ <strong>Заказ поставщику</strong> без проводки;</p></li><li><p><strong>Сохранить</strong> – позволяет сохранить документ <strong>Заказ поставщику</strong> без проводки;</p></li><li><p><strong>Сменить состояние</strong> – содержит выпадающий список команд для изменения состояний документов:</p><ul><li><p><strong>Провести</strong> – позволяет провести документ;</p></li><li><p><strong>Отменить</strong> – позволяет отменить проведение документа;</p></li><li><p><strong>Добавить в Архив</strong> – позволяет добавить документ в <strong>Архив</strong>;</p></li><li><p><strong>Извлечь из Архива</strong> – позволяет извлечь документ из <strong>Архива</strong>;</p></li></ul></li><li><p><strong>Создать платеж</strong> – содержит выпадающий список команд для создания платежей на основании документа:</p><ul><li><p><strong>Расходный кассовый ордер</strong>;</p></li><li><p><strong>Платежное поручение</strong>.</p></li></ul></li><li><p><strong>Создать на основании</strong> – содержит выпадающий список команд для формирования новых документов на основании заказа:</p><ul><li><p><strong>Наш отказ</strong>;</p></li><li><p><strong>Отказ клиента</strong>;</p></li><li><p><strong>Отказ поставщику</strong>;</p></li></ul></li><li><p><strong>Веб-заказ</strong> – позволяет выполнить веб-заказ поставщику**;**</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Подключение веб-сервисов <strong>Веб-проценки</strong>, <strong>Веб-заказ</strong> и <strong>Веб-аналоги</strong> осуществляется по запросу. Условия подключения можно узнать у менеджеров компании Tradesoft. Контактные данные можно найти по адресу <a href="http://www.tradesoft.ru/about/contacts/" target="_blank" rel="noopener noreferrer">www.tradesoft.ru/about/contacts</a>.</p></div><ul><li><strong>Импорт</strong> – команда позволяет загрузить в таблицу (справочник, документы) новые записи из файла.</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Системой поддерживаются следующие форматы файлов для загрузки:</p><ul><li><p>Microsoft Excel 95-2003 (*.xls);</p></li><li><p>Microsoft Excel 2007-2013 (*.xlsx, *.xlsm);</p></li><li><p>Файлы dBase (*.dbf);</p></li><li><p>Текстовые файлы с разделителями &quot;точка с запятой&quot;, &quot;запятая&quot;, &quot;табуляция&quot; (*.txt, *.csv).</p></li></ul></div><ul><li><strong>Экспорт</strong> – команда позволяет экспортировать данные из таблицы в файл и отправить экспортируемые данные по почте.</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Системой поддерживаются следующие форматы выгружаемых файлов:</p><ul><li><p>Файлы *.csv с разделителями &quot;точка с запятой&quot;, &quot;табуляция&quot;.</p></li><li><p>Microsoft Excel 2007 (*.xlsx);</p></li><li><p>Таблица XML 2003 (*.xml);</p></li><li><p>MS Access (*.mdb).</p></li></ul></div><ul><li><p><strong>Связаться</strong> – содержит выпадающий список команд для связи с поставщиком:</p><ul><li><strong>Позвонить</strong> – позволяет** осуществить телефонный вызов поставщику**;**</li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Осуществление вызовов доступно только для пользователей с подключенным модулем <strong>IP-телефония</strong>.</p></div><ul><li><strong>Открыть чат</strong> – позволяет открыть окно <strong>Чата</strong> с поставщиком.</li></ul></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Для работы сервиса отправки уведомлений и сообщений через Telegram-бота необходимо:</p><ul><li><p>подключение дополнительной услуги. За подключением услуги обратитесь в отдел продаж Компании Tradesoft;</p></li><li><p>созданный и настроенный Telegram-бот;</p></li><li><p>установленный и подключенный Сервер Parts.Intellect. Подробнее в разделе <a href="#678abee0-5d3e-466d-8a1b-d556b23a5110">Установка Сервера Parts.Intellect</a>.</p></li></ul><p>Подробнее о работе модуля читайте в <a href="https://product-doc.tradesoft.ru/ai/telegram/index.htm" target="_blank" rel="noopener noreferrer">руководстве пользователя</a>.</p></div><ul><li><strong>Печать</strong> – позволяет открыть окно <strong>Мастер печати</strong>, в котором содержатся шаблоны документов, доступных для печати;</li></ul><p><img src="'+B+'" alt=""></p><ul><li><p><strong>Прочее</strong> – содержит выпадающий список команд:</p><ul><li><p><strong>Навигация по связям</strong> – позволяет отобразить/скрыть панель <strong>Связи документов</strong>;</p></li><li><p><strong>Обновить данные по документу</strong> – позволяет обновить отображаемые в табличной части данные документов.</p></li></ul></li><li><p><img src="'+o+'" alt=""> <strong>Помощь</strong> – позволяет открыть <strong>Руководство пользователя</strong> по разделу.</p></li></ul><p><img src="'+r+'" alt=""> <strong>Основные параметры</strong></p><p><img src="'+U+'" alt=""></p><p>Блок содержит параметры:</p><ul><li><strong>Номер</strong> – порядковый номер документа, доступен ручной ввод.</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Возможность ручного редактирования <strong>Номера документа</strong> для пользователя определяется опцией <strong>Можно редактировать номер документа</strong> в разделе меню <strong>Управление ► Настройки программы ► Роли пользователей ► Документы ► Заказы поставщикам</strong>.</p><p>Формат и счетчик для формирования номера документа определяется правилом в разделе меню <strong>Управление ► Справочники ► Счетчик номеров документов</strong>.</p></div><ul><li><strong>Дата</strong> – дата и время создания документа;</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Для определения даты и времени программа использует данные компьютера пользователя или время компьютера, где расположена база данных, при активной настройке <strong>Использовать локальное время сервера БД</strong> в разделе меню <strong>Управление ► Настройки программы ► Настройки</strong> блок <strong>Системные настройки</strong>.</p><p>Возможность ручного редактирования <strong>Даты</strong> для пользователя определяется опцией <strong>Можно редактировать дату и время документа</strong> в разделе меню <strong>Управление ► Настройки программы ► Роли пользователей ► Документы ► Заказы поставщикам</strong>.</p></div><ul><li><p><strong>Наша фирма –</strong> наша фирма от которой оформляется заказ;</p></li><li><p><strong>Скидка/Наценка, %</strong> – значение процента скидки или наценки (значение меняется при нажатии на имя поля), которое применяется на все позиции документа.</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Доступ к полю определяется разрешением пользователя <strong>Можно изменять скидку/наценку</strong> в разделе меню <strong>Управление ► Настройки программы ► Роли пользователей ► Документы ► Заказы поставщикам</strong>.</p><p>Значение можно указывать в процентах в поле <strong>Скидка (расчетная), %</strong>, или указать непосредственно <strong>Сумму скидки</strong>. Значения полей рассчитываются автоматически на основании значения второго поля.</p></div><ul><li><p><strong>Способ доставки</strong> – укажите способ доставки товара от поставщика;</p></li><li><p><strong>Поставщик</strong> – поставщик, у которого заказывается товар. Поле заполняется автоматически из данных <strong>Поставщик из маркетинга</strong> позиций заказа клиента;</p></li><li><p><strong>Дата отправки заказа</strong> – дата, когда заказ будет размещен;</p></li><li><p><strong>Склад назначения</strong> – склад либо торговая точка, на которую необходимо переместить товар после оприходования. Перемещение осуществляется через вызов мастера <strong>Перемещение товара по ТТ</strong> из списка приходных накладных;</p></li><li><p><strong>Внешний номер</strong> – внешний номер документа.</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Для пользователя может быть разрешено изменения поля <strong>Внешний номер</strong> даже после проводки документа. Разрешение регулируется опцией <strong>Можно изменять внешний номер документа после проводки</strong> в разделе меню <strong>Управление ► Настройки программы ► Роли пользователей ► Документы ► Заказы клиентов</strong>.</p></div><p><img src="'+n+'" alt=""> <strong>Дополнительные параметры</strong></p><p><img src="'+G+'" alt=""></p><p>Блок содержит параметры:</p><ul><li><p><strong>Валюта</strong> – валюта цен в документе. Если изменить валюту документа программа предложит сконвертировать цены на позиции к валюте документа по текущему курсу для валют;</p></li><li><p><strong>Отметка</strong> – выберите отметку для документа из справочника <strong>Отметки в документах</strong>.</p></li><li><p><strong>Стоимость доставки</strong> – укажите влияние стоимости доставки на цену закупа:</p><ul><li><p><strong>Не включать стоимость доставки</strong> – суммарное значение по колонке <strong>Стоимость доставки</strong> не будет влиять на общую сумму документа;</p></li><li><p><strong>Включать стоимость без учета скидки</strong> – сумма колонки <strong>Стоимость доставки</strong> будет прибавлена к сумме по документу;</p></li><li><p><strong>Включать стоимость с учетом скидки</strong> – на стоимость доставки будет действовать <strong>Скидка/Наценка, %</strong> по документу;</p></li></ul></li><li><p><strong>Выделять НДС</strong> – определяет наличие ставки НДС при расчете стоимости позиций, по умолчанию значение берется из карточки <strong>Нашей фирмы</strong>. При необходимости значение можно изменить.</p></li></ul><p><img src="'+g+'" alt=""> <strong>Товары</strong></p><p><img src="'+V+'" alt=""></p><p>Содержит список товаров для заказа поставщику. При создании документа на основании <strong>Заказа клиента</strong>, позиции автоматически будут скопированы из документа-родителя.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Список, порядок и отображение колонок в <strong>Табличной части</strong> настраивается индивидуально каждым пользователем программы. Для отображения/скрытия колонок используется команда <strong>Выбор колонок</strong>, которая открывает одноименное окно.</p></div><p>Доступные колонки по позициям в табличной части <strong>Товары</strong>:</p><ul><li><p><strong>Базовая валюта</strong> – валюта, в которой были указаны цены, на момент добавления позиции в документ;</p></li><li><p><strong>Базовая закупочная цена</strong> – цена закупа из &quot;источника&quot; добавления на момент добавления позиции в документ (обычно цена от поставщика);</p></li><li><p><strong>Базовая цена продажи</strong> – цена продажи из &quot;источника&quot; позиции на момент добавления позиции в документ;</p></li><li><p><strong>Базовая цена продажи в прайс-листе из наличия</strong> (только для <strong>Расширенной</strong> и <strong>Партионной</strong> схемы ЦО) – значение Базовая цена продажи из Прайс-листа из наличия на момент добавления позиции в документ;</p></li><li><p><strong>В наличии на доступных складах</strong> – общее наличие по товару на всех складах, доступных пользователю для работы;</p></li><li><p><strong>В резерве на доступных складах</strong> – общее количество зарезервированного товара на всех складах, доступных пользователю для работы;</p></li><li><p><strong>Валюта</strong> – валюта товара подставляемая на основании <strong>Валюта</strong> документа;</p></li><li><p><strong>Веб-Инфо</strong> – колонка содержит команду вызова окна, содержащего общую информацию и изображения детали из сервиса веб-инфо (по вопросу подключения сервиса можно уточнить информацию в отделе продаж);</p></li><li><p><strong>Внутренний код</strong> – <strong>Внутренний код</strong> из карточки товара;</p></li><li><p><strong>Внутренний код поставщика</strong> – <strong>Внутренний код</strong> товара из прайс-листа поставщика;</p></li><li><p><strong>Возможность возврата</strong> – отображает признак возможности возврата из прайс-листа;</p></li><li><p><strong>Группа товара</strong> – основная группа товара из карточки;</p></li><li><p><strong>Дополнительные свойства товара</strong> – дополнительные свойств товара из карточки;</p></li><li><p><strong>Единица измерения</strong> – указывает <strong>Единица измерения</strong> из карточки товара;</p></li><li><p><strong>Закупочная цена (без НДС)</strong> – цена закупа детали у поставщика без учета НДС;</p></li><li><p><strong>Закупочная цена (с НДС)</strong> – цена закупа детали у поставщика с учетом значения колонки <strong>Ставка НДС, %</strong>;</p></li><li><p><strong>Закупочная цена со скидкой и доставкой (с НДС)</strong> – цена закупа с учетом процента скидки/наценки по документу и НДС;</p></li><li><p><strong>Закупочная цена со скидкой и доставкой (без НДС)</strong> – цена закупа с учетом процента скидки/наценки по документу и без учета НДС** ;</p></li><li><p><strong>Замены товара</strong> – поле заполняется информацией об исходном замененном коде товара в документе (например, если в заказе поставщику был отличный артикул);</p></li><li><p><strong>Идентификатор в сервисе Веб-заказ</strong> – уникальный номер позиции в сервисе Веб-заказ;</p></li><li><p><strong>Исходный производитель</strong> – наименование производителя в том виде, как оно записано в источнике поиска;</p></li><li><p><strong>Итоговая стоимость (без НДС)</strong> – стоимость товара с учетом процента скидки/наценки по документу и значения колонки <strong>Стоимость доставки</strong> (если включен ее учет);</p></li><li><p><strong>Итоговая стоимость (с НДС)</strong> – стоимость товара с учетом процента скидки/наценки по документу, значения колонок <strong>Стоимость доставки</strong> (если включен ее учет) и <strong>Ставка НДС,</strong> % (если выделяется НДС);</p></li><li><p><strong>Категория клиента из маркетинга</strong> – категория клиента из <strong>Маркетинг</strong> (если к позиции применен маркетинг);</p></li><li><p><strong>Код</strong> – артикул детали;</p></li><li><p><strong>Кол-во</strong> – количество товара;</p></li><li><p><strong>Количество по доставке</strong> – количество товаров, партий и т.п. для расчета <strong>Стоимости доставки</strong>;</p></li><li><p><strong>Кратность от поставщика</strong> – коэффициент, согласно которому может быть выбрано количество для заказа от поставщика. Также выполняет роль минимального количества к заказу;</p></li><li><p><strong>Маркетинг</strong> – информация о <strong>Маркетинге</strong>, который применился к позиции при добавлении из <strong>Проценки</strong> или <strong>Корзины</strong>;</p></li><li><p><strong>Меньше минимальной партии от поставщика</strong> – указывает признак если <strong>Кол-во</strong> товара меньше, чем <strong>Минимальная партия от поставщика</strong>;</p></li><li><p><strong>Минимальная партия от поставщика</strong> – минимальное количество единиц товара под заказ в одной партии;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Чтобы настроить разрешение на проведение документа с количеством меньше минимальной партии, в разделе <strong>Управление ► Настройки программы</strong>, вкладка <strong>Роли пользователей ► Разрешения для роли пользователей,</strong> группы настроек <strong>Заказы клиентов, Наши заказы на склад, Заказы поставщикам, VIN-запросы</strong> воспользуйтесь настройкой <strong>Можно проводить с количеством меньше минимальной партии поставщика</strong> для нужных документов и пользователей.</p></div><ul><li><p><strong>Направление/Склад</strong> – направление поставки или склад из прайс-листа;</p></li><li><p><strong>Наценка, %</strong> – наценка на товары в отношении закупочной цены к цене продажи по документу;</p></li><li><p><strong>Не соответствует кратности от поставщика</strong> – отметка о несоответствии кратности товаров от поставщика;</p></li><li><p><strong>Отправка на сайт поставщика</strong> – отображает отметку об отправке товара на сайт поставщика с его количеством;</p></li><li><p><strong>Плановая дата доставки</strong> – дата и время, до которой планируется доставить товар;</p></li><li><p><strong>Подакцизный товар</strong> – указывает признак <strong>Подакцизный товар</strong> из карточки товара;</p></li><li><p><strong>Примечания</strong> – примечание к позиции;</p></li><li><p><strong>Приоритет цен с НДС</strong> – позволяет установить/снять приоритет цен с НДС, который определяет ключевую цену, на основании которой будет высчитываться вторая цена при переключении в параметрах документа значения <strong>Выделять НДС</strong>;</p></li><li><p><strong>Референс от поставщика</strong> – содержит информацию по товару от поставщика;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>В поле <strong>Референс от поставщика</strong> содержится информация от поставщика для идентификации позиции, например, информацию по которой понимает по какому прайс-листу/договору оформляется заказ.</p></div><ul><li><p><strong>Системный штрихкод</strong> – штрихкод позиции документа, присваиваемый системой;</p></li><li><p><strong>Состояние позиции</strong> – состояние (и подсостояние) позиции;</p></li><li><p><strong>Список клиентов</strong> – перечень клиентов, для которых пришел товар (при наличии связанных документов);</p></li><li><p><strong>Ставка НДС, %</strong> – значение ставки в процентах;</p></li><li><p><strong>Статус</strong> – статус товара;</p></li><li><p><strong>Стоимость без скидки (с НДС)</strong> – стоимость товара без скидки с учетом НДС;</p></li><li><p><strong>Стоимость доставки</strong> – сумма по доставке товара (<strong>Цена доставки</strong> * <strong>Количество по доставке</strong>);</p></li><li><p><strong>Сумма НДС</strong> – сумма НДС по позиции с учетом процента скидки/наценки;</p></li><li><p><strong>Товар</strong> – наименование детали (из источника позиции);</p></li><li><p><strong>Товар. Альтернативное название</strong> – <strong>Альт. наименование</strong> указанное в карточке товара;</p></li><li><p><strong>Товар. Производитель</strong> – <strong>Производитель</strong> товара из карточки товара;</p></li><li><p><strong>Товар. Стандартная наценка</strong> – <strong>з</strong>начение наценки и карточки товара;</p></li><li><p><strong>Цена доставки</strong> – значение из справочника <strong>Стоимости доставок</strong>;</p></li><li><p><strong>Цена закупа в прайс-листе из наличия</strong> (только для <strong>Расширенной</strong> схемы ценообразования) – отображает <strong>Цена закупа</strong> из прайс-листа наличия;</p></li><li><p><strong>Цена продажи (без НДС)</strong> – цена продажи, если она присутствовала в <strong>Источник позиции</strong> или была введена вручную;</p></li><li><p><strong>Цена продажи (с НДС)</strong> – цена продажи, если она присутствовала в <strong>Источник позиции</strong> или была введена вручную, с учетом процента скидки/наценки по документу (если выделяется НДС);</p></li><li><p><strong>Цена продажи со скидкой и доставкой (без НДС)</strong> – цена продажи, если она присутствовала в <strong>Источник позиции</strong> или была введена вручную, с учетом процента скидки/наценки по документу, значения колонок <strong>Стоимости доставки</strong> (если включен ее учет) и <strong>Ставка НДС,</strong> <strong>%</strong> (если выделяется НДС);</p></li><li><p><strong>Цена продажи со скидкой и доставкой (c НДС)</strong> – цена продажи, если она присутствовала в <strong>Источник позиции</strong> или была введена вручную, с учетом процента скидки/наценки по документу, значения колонки <strong>Стоимости доставки</strong> (если включен ее учет);</p></li><li><p><strong>Штрихкод внутренний</strong> – внутренний штрихкод из карточки товара.</p></li></ul><p><img src="'+i+'" alt=""> <strong>Панель инструментов табличной части: Товары</strong></p><p><img src="'+S+`" alt=""></p><p>Содержит:</p><ul><li><p><strong>Добавить</strong> – позволяет добавить позиции в табличную часть из нескольких источников:</p><ul><li><p><strong>Из справочника</strong> – позиция будет добавлена в документ без цен из справочника <strong>Товары</strong>;</p></li><li><p><strong>Из справочника через проценку –</strong> позицию можно будет выбрать вначале в справочнике товаров, а затем она будет передана в <strong>Проценку</strong> для подбора предложений;</p></li><li><p><strong>Из проценки</strong> – позволяет добавить позиции из проценки в табличную часть;</p></li><li><p><strong>Из корзины</strong> – позиции будут добавлены из корзины;</p></li><li><p><strong>Из мастера</strong> – открывает окно <strong>Мастер создания расходной накладной</strong> с фильтрацией по <strong>Наша фирма</strong> и <strong>Клиент</strong>;</p></li></ul></li><li><p><strong>Удалить</strong> – позволяет удалить позицию из табличной части;</p></li><li><p><strong>Действия с позицией</strong> – позволяет произвести действия над позицией:</p><ul><li><strong>Добавить в корзину</strong> – добавляет товар из табличной части в корзину;</li></ul></li><li><p><strong>НДС</strong> – содержит выпадающий список с командами для управления НДС:</p><pre><code>- **Изменить ставку НДС** – позволяет изменить ставку НДС для выбранных позиций;

- **Установить приоритет цен с НДС/Снять приоритет цен с НДС  –** позволяет установить/снять приоритет цен с НДС, который определяет ключевую цену, на основании которой будет высчитываться вторая цена при переключении в параметрах документа значения **Выделять НДС**:

   - если для позиции активирована опция **Установить приоритет цен с НДС**, то при включении переключателя **Выделять НДС** значение в колонке **Цена с НДС** не изменится, а **Цена без НДС** рассчитается на ее основании; 

   - убрать приоритет цен можно с помощью опции **Снять приоритет цен с НДС**, если приоритет снят, то при включении переключателя **Выделять НДС** значение в колонке **Цена без НДС** не изменится, а **Цена с НДС** рассчитается на ее основании.
</code></pre></li><li><p><strong>Заполнить маркетинг</strong> – Позволяет автоматически подставить информацию о позиции из существующих маркетингов. Доступна подстановка:</p><ul><li><p><strong>Закупочной цены</strong> <strong>(с НДС)</strong> – цена закупа детали у поставщика;</p></li><li><p><strong>Цены продажи (с НДС)</strong> – цена на товар с учетом маркетинга;</p></li><li><p><strong>Направления/ склада</strong> – направление поставки или склад из прайс-листа;</p></li><li><p><strong>Приоритетного склада для списания</strong> – склад, с которого требуется осуществлять списание товара.</p></li></ul></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Заполнить маркетинг можно как для одной позиции из документа, нажав кнопку <strong>Обновить</strong>, так и для всех, нажав кнопку <strong>Для всех</strong> в открывшемся окне.</p><p>Функция <strong>Заполнить маркетинг</strong> доступна только для позиций с пустым полем <strong>Маркетинг</strong> из источников: <strong>Прайс-листов наличия</strong> и <strong>Прайс-лист поставщика</strong>. Функция активна только для не проведенных документов.</p><p>Для подстановки цен по позиции выполняется проценка с поиском позиции в источниках (из маркетинга, под заказ, из наличия, в пути). Проценка осуществляется в 3 этапа, поиск позиции осуществляется по точному совпадению, поиск по аналогам и синонимам не осуществляется:</p><ul><li><p><strong>1 этап</strong>:</p><ul><li><p>Проверка источника на доступность (в соответствии с разрешениями пользователя в разделе <strong>Управление</strong> <strong>► Настройки программы ► Разрешения для роли пользователей ►  Прайс-листы</strong>;</p></li><li><p>Получение товара из прайс-листа;</p></li><li><p>Загрузка списков маркетингов;</p></li><li><p>Разбитие позиции по складам;</p></li><li><p>Выбор подходящего маркетинга (проверка по диапазону цен, группе скидок, производителю, группе товаров и т.д.);</p></li><li><p>Для товаров в наличии: исключение позиций с неподходящим складом/ Торговой точкой, на которой расположен товар (в соответствии с выбранным Складом/ Торговой точкой в документе);</p></li><li><p>Исключение маркетингов, не подходящих по категории клиента;</p></li><li><p>Для каждого подходящего маркетинга высчитывается цена;</p></li><li><p>Выбор одного маркетинга по весам.</p></li></ul></li><li><p><strong>2 этап</strong>:</p><ul><li>Удаление дубликатов из списка процененных позиций товара;</li></ul></li><li><p><strong>3 этап</strong>:</p><ul><li>Возврат списка процененных позиций из разных источников. В качестве результата берется первый из списка товар.</li></ul></li></ul><p>Для партионной СЦО выполняется сортировка по дате из <strong>Приходной накладной</strong>. Выбор позиции товара из проценки зависит от настройки <strong>Способ списание товара</strong> (<strong>ЛИФО</strong>, <strong>ФИФО</strong>), который задается в разделе <strong>Управление ► Настройки программы ► Настройки ►</strong> группа <strong>Склад и закупки ► Движение товара</strong>. Если в списке присутствуют только позиции из <strong>Прайс-листов поставщиков</strong>, по которым нет <strong>Приходных накладных</strong> – сортировка не выполняется.</p></div><ul><li><strong>Прочее</strong> – позволяет отобразить информацию о документе, а так же отобразить форму <strong>Изображения</strong> с привязанными к позициям изображениям.</li></ul><p><img src="`+p+'" alt=""> <strong>Найти/Добавить</strong></p><p><img src="'+q+'" alt=""></p><p>Поле предназначено для поиска товара или добавления найденной позиции через проценку по коду детали.</p><p><img src="'+l+'" alt=""> <strong>Строка информации</strong></p><p><img src="'+f+'" alt=""></p><p>Отображает информацию по позициям в документе:</p><ul><li><p><strong>Всего товаров</strong> – количество товаров добавленных в табличную часть;</p></li><li><p><strong>Сумма итого</strong> – суммарная стоимость товаров добавленных в табличную часть;</p></li><li><p><strong>Сумма НДС</strong> – суммарное значение НДС по всем позициям товаров в табличной части;</p></li><li><p><strong>Сумма доставки</strong> – суммарная стоимость доставки для всех товаров.</p></li></ul><p><img src="'+A+'" alt=""> <strong>Общее</strong></p><p><img src="'+X+'" alt=""></p><p>Блок содержит поле ввода <strong>Комментария</strong>, которое позволяет оставить комментарий к <strong>Заказ поставщику</strong>.</p><p><img src="'+a+'" alt=""> <strong>Задачи</strong></p><p><img src="'+w+'" alt=""></p><p>Блок содержит информацию о <strong>Контрагенте</strong>:</p><ul><li><p><strong>Контрагент</strong> – отображает следующую информацию:</p><ul><li><p>наименование контрагента;</p></li><li><p><strong>Баланс по заказам</strong> – является суммой значений баланса <strong>Активные заказы</strong> и <strong>Баланса фактического</strong>. Указывает на возможность контрагента оплатить свои заказы;</p></li><li><p><strong>Баланс фактический</strong> – отражает фактический баланс между накладными, возвратами, корректировками и платежными документами;</p></li><li><p><strong>Баланс просроченный</strong> – отражает объем отгрузок (<strong>Расходных накладных</strong>), просроченных по оплате в соответствии с значением поля <strong>Отсрочка платежа</strong> для клиента;</p></li><li><p><strong>Баланс по возвратам</strong> – отражает ситуацию по возвратам, выделяя из <strong>Баланса фактического</strong> сумму возвратов по товарам с учетом платежей;</p></li><li><p><strong>Код контрагента</strong> – выводит уникальный номер контрагента;</p></li><li><p><strong>Категория клиента</strong> – отображает категорию, в которой состоит клиент;</p></li></ul></li><li><p><strong>Блокировка</strong> – статус блокировки контрагента;</p></li><li><p><strong>Контакты</strong> – контактные данные контрагента.</p></li></ul><p><img src="'+e+'" alt=""> <strong>Список платежей</strong></p><p><img src="'+I+'" alt=""></p><p>Отображает список платежных документов по документу заказ поставщику.</p><p><img src="'+M+'" alt=""></p><p><img src="'+c+'" alt=""> <strong>Журнал действий</strong></p><p><img src="'+k+'" alt=""></p><p>Данный журнал отражает действия всех пользователей с документом.</p><p><img src="'+E+'" alt=""></p><p><img src="'+m+'" alt=""> <strong>Журнал уведомлений</strong></p><p><img src="'+K+'" alt=""></p><p>Журнал содержит информацию о работе системы уведомлений клиентов и менеджеров, через Email, SMS и Telegram по документу. В журнал выводятся все события по отправке уведомлений, как успешные, так и нет.</p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Отправка уведомлений через приложение Telegram доступно только для пользователей с подключенным модулем.</p></div><p><img src="'+O+'" alt=""></p>',74)]))}const H=u(y,[["render",D]]),_=JSON.parse('{"path":"/specification/sklad_i_zakupki/novyj_zakaz_postavschiku.html","title":"Новый Заказ поставщику","lang":"en-US","frontmatter":{"title":"Новый Заказ поставщику","createTime":"2025/04/23 14:55:54"},"headers":[],"readingTime":{"minutes":9.04,"words":2711},"git":{"updatedTime":1750169605000,"contributors":[{"name":"a.lutoshkin","username":"","email":"a.lutoshkin@tradesoft.ru","commits":2,"avatar":"https://gravatar.com/avatar/605ee9d365e3a6380f8e9c71f9269ee5373d537aebefbece4e023cac396dfc8d?d=retro"},{"name":"bONK-cup","username":"bONK-cup","email":"kokosik260302@gmail.com","commits":1,"avatar":"https://avatars.githubusercontent.com/bONK-cup?v=4","url":"https://github.com/bONK-cup"}]},"filePathRelative":"specification/sklad_i_zakupki/novyj_zakaz_postavschiku.md","categoryList":[{"id":"fd8b54","sort":10022,"name":"specification"},{"id":"3d290e","sort":10036,"name":"sklad_i_zakupki"}]}');export{H as comp,_ as data};
