import{_ as s}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.004-D-lAiJXD.js";import{_ as o}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.006-C6TVXSfy.js";import{_ as r}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.008-CELUo195.js";import{_ as n}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.010-DDUYNioq.js";import{_ as g}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.017-57kAEd1K.js";import{_ as A}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.019-CQSyaFwC.js";import{_ as p}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.695-IjuUuEv9.js";import{_ as i,a as l}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.721-gy2PuPXm.js";import{_ as a}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.021-QBaRAmgp.js";import{_ as e}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.023-C7RHCIAC.js";import{_ as m,a as c}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.028-Cpi_m1_R.js";import{_ as d}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.030-Cy6WJzwU.js";import{_ as u}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.033-D067HoDi.js";import{_ as E,c as Q,a as B,o as b}from"./app-CUu1n7_5.js";const f="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.689-BKkMBQIF.png",h="data:image/png;base64,",T="data:image/png;base64,",X="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.692-BPbltnoX.png",j="data:image/png;base64,",V="data:image/png;base64,",I="data:image/png;base64,",v="data:image/png;base64,",D="data:image/png;base64,",R="data:image/png;base64,",W="data:image/png;base64,",C="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.704-CcUmg8tk.png",U={};function x(N,t){return b(),Q("div",null,t[0]||(t[0]=[B('<p>Мастер позволяет быстро сформировать документ <strong>Приходная накладная</strong> на товары, требующие оприходования.</p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p><strong>Мастер создания Приходной накладной</strong> осуществляет поиск и подсчитывает количество товара только из документов**,** на основании которых может быть создана **Приходная накладная.**В колонке <strong>Остаток</strong> будет выведено количество только того товара, состояние которого соответствует значению в столбце <strong>Состояние</strong>.</p></div><p><img src="'+f+'" alt=""></p><p><img src="'+s+'" alt=""> <strong>Фильтр по дате</strong></p><p><img src="'+h+'" alt=""></p><p><strong>Фильтр по дате</strong> позволят указать временной промежуток за который необходимо отобразить документы для поиска.</p><p><img src="'+o+'" alt=""> <strong>Дополнительный фильтр</strong></p><p><img src="'+T+'" alt=""></p><p><strong>Дополнительный фильтр</strong> позволят указать названия товаров либо номера документов через точку с запятой (😉. Если известна лишь часть названия товара либо номера документа, то допускается поиск по части слова. После ввода нажать кнопку <strong>Обновить</strong> в блоке <strong>Доступные позиции</strong>.</p><p><img src="'+r+'" alt=""> <strong>Контрагенты/Места хранения</strong></p><p><img src="'+X+'" alt=""></p><p>Фильтр <strong>Контрагенты/Места хранения</strong> позволят указать по кому будет осуществлена фильтрация документов с товарами: по <strong>Поставщику</strong> или <strong>Клиенту</strong>. Так же указывается, где будет осуществлен поиск: <strong>Наша фирма</strong>, <strong>Склад</strong>.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Выбор складов осуществляется из списка складов, доступных для работы текущему пользователю программы.</p></div><p><img src="'+n+'" alt=""> <strong>Поиск детали</strong></p><p><img src="'+j+'" alt=""></p><p>Блок позволяет указать свойство товара по которому необходимо осуществить поиск и осуществить поиск детали.</p><p>Доступные для выбора свойства:</p><ul><li><p><strong>Код товара: строгий поиск</strong> – производится строгий поиск доступных позиций по указанному коду товара;</p></li><li><p><strong>Код товара: по вхождению</strong> – производится поиск доступных позиций по указанной части кода товара;</p></li><li><p><strong>Штрихкод производителя</strong> – производится поиск доступных позиций по указанной части штрихкода производителя товара;</p></li><li><p><strong>Референс от клиента</strong> – производится поиск доступных позиций по референсу товара от клиента;</p></li><li><p><strong>СШ позиции ЗК</strong> – производится поиск доступных позиций по указанному системному штрихкоду товара из заказа клиента;</p></li><li><p><strong>Референс от поставщика</strong> – производится поиск доступных позиций по референсу товара от поставщика;</p></li><li><p><strong>СШ позиции ЗП</strong> – производится поиск доступных позиций по указанному системному штрихкоду товара из заказа поставщику.</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Детали, которые участвуют в поиске, автоматически попадают в <strong>Выбранные позиции</strong>. Это сделано на случай, когда пользователь системы считывает сканером код товара для занесения в выбранные позиции.</p></div><p><img src="'+g+'" alt=""> <strong>Обновить</strong></p><p><img src="'+V+'" alt=""></p><p>Позволяет обновить список доступных позиций до актуального состояния.</p><p><img src="'+A+'" alt=""> <strong>Доступные позиции</strong></p><p>Позиции доступные для создания документа.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Список, порядок и отображение колонок в <strong>Табличной части</strong> настраивается индивидуально каждым пользователем программы. Для отображения/скрытия колонок используется команда <strong>Выбор колонок</strong>, которая открывает одноименное окно.</p></div><p>Доступные колонки по позициям в блоке <strong>Доступные позиции</strong>:</p><ul><li><p><strong>№ ГТД</strong> – номер грузовой таможенной декларации, если деталь была импортирована в страну;</p></li><li><p><strong>№ документа</strong> – номер документа;</p></li><li><p><strong>Автор</strong> – автор документа;</p></li><li><p><strong>В наличии на доступных складах</strong> – общее наличие по товару на всех складах, доступных пользователю для работы;</p></li><li><p><strong>Валюта</strong> – валюта цен в документе;</p></li><li><p><strong>Внутренний код – Внутренний код</strong> из карточки товара;</p></li><li><p><strong>Внутренний код поставщика – Внутренний код</strong> товара из прайс-листа поставщика;</p></li><li><p><strong>Возможность возврата</strong> – отображает признак возможности возврата товара из прайс-листа;</p></li><li><p><strong>Город клиента</strong> – город клиента указанный в карточке контрагента;**</p></li><li><p><strong>Группа товара</strong> – основная группа товара из карточки;</p></li><li><p><strong>Дата</strong> – дата и время создания документа;</p></li><li><p><strong>Документ</strong> – наименование, дата и номер документа с позицией доступной для создания приходной накладной;</p></li><li><p><strong>Дополнительные свойства товара</strong> – дополнительные свойства товара из карточки;</p></li><li><p><strong>Закупочная цена со скидкой и доставкой (с НДС)</strong> – цена закупа с учетом процента скидки/наценки по документу и НДС;</p></li><li><p><strong>Идентификатор места хранения</strong> – идентификатор <strong>Место хранения</strong> указанный в карточке <strong>Склады/Торговые точки</strong> раздела <strong>Склад и закупки</strong>;</p></li><li><p><strong>Клиент – Клиент</strong> указанный в документе;</p></li><li><p><strong>Код</strong> – артикул детали;</p></li><li><p><strong>Код контрагента</strong> – код клиента из справочника <strong>Контрагенты</strong>;</p></li><li><p><strong>Маркетинг</strong> – информация о <strong>Маркетинге</strong>, который применился к позиции при добавлении из <strong>Проценки</strong> или <strong>Корзины</strong>;</p></li><li><p><strong>Место хранения</strong> – склад/торговая точка/место хранения где находится позиция;</p></li><li><p><strong>Мобильный телефон – Мобильный телефон</strong> указанный в карточке контрагента;</p></li><li><p><strong>Направление/Склад</strong> – направление поставки или склад из прайс-листа;</p></li><li><p><strong>Наша фирма</strong> – наша фирма на которую оформлен документ;</p></li><li><p><strong>Общее наличие на складах</strong> – общее наличие товара на всех складах, в том числе недоступных пользователю;</p></li><li><p><strong>Остаток</strong> – текущий остаток на складе в указанном состоянии;</p></li><li><p><strong>Ответственный менеджер</strong> – ответственный менеджер клиента из справочника <strong>Контрагенты</strong>;</p></li><li><p><strong>Отметка</strong> – отметка документа из справочника <strong>Отметки в документах</strong>;</p></li><li><p><strong>Плановая дата доставки</strong> – отображает дату доставки детали;</p></li><li><p><strong>Подсостояние</strong> – подсостояние позиции документа;</p></li><li><p><strong>Поставщик</strong> – поставщик** из справочника <strong>Контрагенты</strong>;</p></li><li><p><strong>Поставщик из маркетинга</strong> – поставщик из <strong>Маркетинг</strong> (если к позиции применен маркетинг);</p></li><li><p><strong>Примечания</strong> – примечание к позиции;</p></li><li><p><strong>Производитель – Производитель</strong> указанный в карточке товара;</p></li><li><p><strong>Рабочий телефон – Рабочий телефон</strong> клиента из карточки контрагента;</p></li><li><p><strong>Референс от клиента</strong> – содержит информацию по товару от клиента;</p></li><li><p><strong>Референс от поставщика</strong> – содержит информацию по товару от поставщика;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>В полях <strong>Референс от клиента</strong> и <strong>Референс от поставщика</strong> содержится информация от клиента или поставщика для идентификации позиции, например,** информацию по которой понимает по какому прайс-листу поставщика/договору оформляется заказ.</p></div><ul><li><p><strong>Склад назначения/место выдачи</strong> – торговая точка/склад, с которого предполагается выдавать товар клиенту;</p></li><li><p><strong>Состояние</strong> – состояние позиций документа;</p></li><li><p><strong>Страна – Страна</strong> товара из справочника <strong>Страны</strong> раздела <strong>Управление ► Словари и классификаторы</strong>;</p></li><li><p><strong>Сумма долга</strong> – сумма задолженности по документу;</p></li><li><p><strong>СШ позиции ЗК</strong> – системный штрихкод позиции по документу <strong>Заказ клиента</strong>;</p></li><li><p><strong>СШ позиции ЗП</strong> – системный штрихкод позиции по документу <strong>Заказ поставщику</strong>;</p></li><li><p><strong>Тип документа</strong> – тип документа на основании которого выделены доступные позиции;</p></li><li><p><strong>Товар</strong> – наименование детали (из источника позиции);</p></li><li><p><strong>Цена продажи со скидкой и доставкой (с НДС)</strong> – цена продажи, если она присутствовала в <strong>Источник позиции</strong> или была введена вручную, с учетом процента скидки/наценки по документу, значения колонки <strong>Стоимости доставки</strong> (если включен ее учет).</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Помимо штатных фильтров, расположенных на панели <strong>Основной фильтр</strong>, присутствует возможность фильтрации позиций с помощью <strong>Конструктора фильтров</strong>. Данный инструмент значительно расширяет возможности мастеров. <strong>Конструктор фильтров</strong> вызывается с помощью одноименной команды контекстного меню при клике на колонку табличной части.<img src="'+p+'" alt=""></p></div><p>Из мастера возможно осуществить редактирование данных в колонках:</p><ul><li><p><strong>Документ</strong>;</p></li><li><p><strong>Наша фирма</strong>;</p></li><li><p><strong>Клиент</strong>;</p></li><li><p><strong>Поставщик</strong>;</p></li><li><p><strong>Товар</strong>;</p></li><li><p><strong>Поставщик из маркетинга</strong>.</p></li></ul><p>Редактирование осуществляется путем нажатия на галочку в необходимой ячейке, после чего откроется соответствующая форма для редактирования.</p><p><img src="'+i+'" alt=""></p><p><img src="'+a+'" alt=""> <strong>Выбрать</strong></p><p><img src="'+I+'" alt=""></p><p>Позволяет добавить выбранные позиции из блока <strong>Доступные позиции</strong> в блок <strong>Выбранные позиции</strong>.</p><p><img src="'+e+'" alt=""> <strong>Импорт...</strong></p><p><img src="'+v+'" alt=""></p><p>Позволяет отобразить позиции мастера <strong>Из файла</strong> или документа <strong>Задание на разбор товара</strong>.</p><p><img src="'+D+'" alt=""></p><p>Программа сверит товары из файла/задания и список позиций из блока <strong>Доступные позиции</strong>:</p><ul><li><p>Если товар есть в файле/задании и в позициях мастера, то он добавится в выбранные позиции, следуя правилам:</p><ul><li><p>Если количество искомого товара из файла/задания совпадает с количеством в позициях этого же товара в мастере, то товар попадает в список выбранных. Причем, если в мастере найдено несколько позиций с требуемым количеством, то отбирается позиция с максимально близкой ценой к цене из файла/задания;</p></li><li><p>Если позиции в мастере с совпадающим количеством отсутствуют, то товар из файла/задания равномерно &quot;расходует&quot; количество у доступных позиций мастера.</p></li></ul></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>В <strong>Мастере создания Приходной накладной</strong> позиции из наиболее ранних <strong>Заказов клиента</strong> являются приоритетными.</p></div><ul><li>Если товар присутствует в файле/задании, но отсутствует в позициях мастера, то такие позиции будут отфильтрованы и выведены в файл-журнал процесса.</li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Если включена опция <strong>Использовать Оригинальные замены в МПН</strong> (меню <strong>Управление ► Настройки программы ► Настройки</strong>, группа <strong>Склад и закупки ► Закупки</strong>), то подбор товара в мастере осуществляется с учетом следующей логики:</p><ul><li><p>если товар из файла/задания не был найден в списке ожидаемых позиций мастера, то программа для этого товара через справочник <strong>Оригинальных замен</strong> ищет устаревшие артикулы;</p></li><li><p>затем программа ищет среди ожидаемых позиций мастера товар с устаревшим артикулом;</p></li><li><p>затем устаревшим артикулом по отношению к устаревшему и так далее;</p></li></ul><p>Таким образом, программа, используя справочник <strong>Оригинальных замен производителей</strong>, пытается определить, что пришла замена по товару.</p></div><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Дополнительной возможностью при импорте из файла является выбор в разметке колонок <strong>Код контрагента</strong> и <strong>Номер Заказа поставщику</strong>, по которым программа автоматически понимает, для какого клиента и из какого <strong>Заказа поставщику</strong> пришел товар.</p><p>Так же доступна возможность подбора позиций с помощью разметки одного из четырех референсов:</p><ul><li><p><strong>референс от клиента;</strong></p></li><li><p><strong>СШ позиции ЗК;</strong></p></li><li><p><strong>референс от поставщика;</strong></p></li><li><p><strong>СШ позиции ЗП.</strong></p></li></ul></div><p><img src="'+m+'" alt=""> <strong>Очистить</strong></p><p><img src="'+R+'" alt=""></p><p>Позволяет отчистить список от одной или нескольких выбранных позиций. Также доступна полная очистка списка по кнопке <strong>Очистить всё</strong>.</p><p><img src="'+l+'" alt="ref53"></p><p><img src="'+c+'" alt=""> <strong>Выбранные позиции</strong></p><p>Позиции которые выбраны для создания документа.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Детали, которые участвуют в поиске, автоматически попадают в <strong>Выбранные позиции</strong>. Это сделано на случай, когда пользователь системы считывает сканером код товара для занесения в выбранные позиции.</p><p>Список, порядок и отображение колонок в <strong>Табличной части</strong> настраивается индивидуально каждым пользователем программы. Для отображения/скрытия колонок используется команда <strong>Выбор колонок</strong>, которая открывает одноименное окно.</p></div><p>Доступные колонки по позициям в табличной части <strong>Выбранные позиции</strong>:</p><ul><li><p><strong>№ ГТД</strong> – номер грузовой таможенной декларации, если деталь была импортирована в страну;</p></li><li><p><strong>№ счета-фактуры</strong> – номер счета-фактуры указанный в документе;</p></li><li><p><strong>Валюта</strong> – валюта цен в документе;</p></li><li><p><strong>Внутренний код поставщика – Внутренний код</strong> товара из прайс-листа поставщика;</p></li><li><p><strong>Возможность возврата</strong> – отображает признак возможности возврата товара из прайс-листа;</p></li><li><p><strong>Дата счета-фактуры</strong> – дата и время создания счета-фактуры;</p></li><li><p><strong>Документ проведен</strong> – указывает признак проведенности документа;</p></li><li><p><strong>Закупочная цена</strong> – цена закупа детали у поставщика;</p></li><li><p><strong>Замена. Код детали</strong> – артикул детали замены;</p></li><li><p><strong>Клиент – Клиент</strong> указанный в документе;</p></li><li><p><strong>Код детали</strong> – артикул детали;</p></li><li><p><strong>Код ТНВЭД от поставщика</strong> – код ТНВЭД из прайс-листа поставщика;</p></li><li><p><strong>Кол-во</strong> – количество товара;</p></li><li><p><strong>Маркетинг</strong> – информация о <strong>Маркетинге</strong>, который применился к позиции при добавлении из <strong>Проценки</strong> или <strong>Корзины</strong>;</p></li><li><p><strong>Место хранения</strong> – склад/торговая точка/место хранения где находится позиция;</p></li><li><p><strong>Наименование товара от поставщика</strong> – название товара из прайс-листа поставщика;</p></li><li><p><strong>Наша фирма</strong> – наша фирма на которую оформлен документ;</p></li><li><p><strong>Плановая дата доставки</strong> – отображает дату доставки детали;</p></li><li><p><strong>Поставщик</strong> – поставщик** из справочника <strong>Контрагенты</strong>;</p></li><li><p><strong>Примечания</strong> – примечание к позиции;</p></li><li><p><strong>Производитель – Производитель</strong> указанный в карточке товара;</p></li><li><p><strong>Состояние</strong> – состояние позиций документа;</p></li><li><p><strong>Ставка НДС</strong> – значение ставки в процентах;</p></li><li><p><strong>Старый склад/Место хранения</strong> – старый склад/место хранения позиции товара;</p></li><li><p><strong>Статус</strong> – статус товара;</p></li><li><p><strong>Стоимость (вкл НДС)</strong> – стоимость товара с учетом процента скидки/наценки по документу (если выделяется НДС);</p></li><li><p><strong>Страна – Страна</strong> товара из справочника <strong>Страны</strong> раздела <strong>Управление ► Словари и классификаторы</strong>;</p></li><li><p><strong>Сумма НДС</strong> – сумма НДС по позиции с учетом процента скидки/наценки;</p></li><li><p><strong>Товар</strong> – наименование детали (из источника позиции);</p></li><li><p><strong>Цена продажи</strong> – цена продажи товара с учетом процента скидки/наценки по документу (если выделяется НДС);</p></li><li><p><strong>Цена продажи без НДС</strong> – цена продажи товара без учета НДС.</p></li></ul><p>Для каждого столбца доступны несложные расчеты. Для этого нажать правой клавишей мыши на панели под столбцом и выбрать требуемый алгоритм: <strong>Сумма</strong>, <strong>Минимум</strong>, <strong>Максимум</strong>, <strong>Количество</strong>, <strong>Среднее</strong>, <strong>Нет</strong>.</p><p>![ref54]</p><p>После выбора алгоритма доступен выбор <strong>Режима</strong> расчета:</p><ul><li><p><strong>Все строки</strong> – значение будет рассчитываться по всем строкам;</p></li><li><p><strong>Выбранные</strong> – значение будет рассчитываться по выбранным строкам;</p></li><li><p><strong>Смешанный</strong> – значение может быть рассчитано по выбранным строкам, если выбрано больше одной строки или по всем, если выбрано только одна строка или не выбрано ни одной.</p></li></ul><p><img src="'+d+'" alt=""> <strong>Действие с новыми документами</strong></p><p>Позволяет выбрать действие, которое будет выполнено над сформированным документом:</p><ul><li><p><strong>Открыть в редакторе</strong> – выбранные товары и другие атрибуты будут переданы на форму редактирования документа;</p></li><li><p><strong>Сохранить</strong> – будет создан и сохранен соответствующий документ;</p></li><li><p><strong>Сохранить</strong> <strong>и провести</strong> – будет создан, сохранен и проведен соответствующий документ;</p></li><li><p><strong>Сохранить, провести и напечатать</strong> – будет создан, сохранен, проведен и отправлен на печать соответствующий документ;</p></li><li><p><strong>Сохранить, провести и отправить по почте</strong> – будет создан, сохранен, проведен и отправлен по почте соответствующий документ.</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>При выполнении действия <strong>Сохранить, провести и отправить по почте</strong> после создания документа на почту будет отправлен файл с позициями документа. Перед отправкой необходимо указать колонки, которые будут экспортированы.</p></div><p><img src="'+u+'" alt=""> <strong>Создать</strong></p><p><img src="'+W+'" alt=""></p><p>Позволяет сформировать новые документы <strong>Приходная накладная</strong> или <strong>Задание на разбор товара</strong>.</p><p><img src="'+C+'" alt=""></p><p>Также доступны действия:</p><ul><li><p><strong>Очистить позиции после создания</strong> – позволяет очистить <strong>Выбранные позиции</strong> от товаров после создания документа;</p></li><li><p><strong>Для выделенных позиций</strong> – позволяет создать новый документ только для выделенных пользователем позиций.</p></li></ul>',70)]))}const k=E(U,[["render",x]]),G=JSON.parse('{"path":"/specification/sklad_i_zakupki/master_sozdanija/prihodnaja_nakladnaja.html","title":"Приходная накладная","lang":"en-US","frontmatter":{"title":"Приходная накладная","createTime":"2025/04/29 11:50:45"},"headers":[],"readingTime":{"minutes":6.2,"words":1859},"git":{"updatedTime":1745929570000,"contributors":[{"name":"a.lutoshkin","username":"","email":"a.lutoshkin@tradesoft.ru","commits":1,"avatar":"https://gravatar.com/avatar/605ee9d365e3a6380f8e9c71f9269ee5373d537aebefbece4e023cac396dfc8d?d=retro"}]},"filePathRelative":"specification/sklad_i_zakupki/master_sozdanija/prihodnaja_nakladnaja.md","categoryList":[{"id":"fd8b54","sort":10022,"name":"specification"},{"id":"3d290e","sort":10036,"name":"sklad_i_zakupki"},{"id":"0d625a","sort":10038,"name":"master_sozdanija"}]}');export{k as comp,G as data};
