import{_ as A}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.004-D-lAiJXD.js";import{_ as o}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.006-C6TVXSfy.js";import{_ as s}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.008-CELUo195.js";import{_ as r}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.010-DDUYNioq.js";import{_ as n}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.017-57kAEd1K.js";import{_ as g}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.019-CQSyaFwC.js";import{_ as i}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.021-QBaRAmgp.js";import{_ as p}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.023-C7RHCIAC.js";import{_ as l,a}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.028-Cpi_m1_R.js";import{_ as e}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.030-Cy6WJzwU.js";import{_ as c}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.033-D067HoDi.js";import{_ as m}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.035-CZPeNSOK.js";import{_ as u}from"./Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.038-BdANhsvJ.js";import{_ as E,c as d,a as C,o as B}from"./app-CUu1n7_5.js";const Q="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.386-cT-sXYD4.png",w="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.387-DIABjGs5.png",I="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.388-Cz3HrtSx.png",h="data:image/png;base64,",f="data:image/png;base64,",R="data:image/png;base64,",S="data:image/png;base64,",v="data:image/png;base64,",q="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAIAAACQkWg2AAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAS0lEQVQokWPUjp/EQApgYWBgmFCdQKTqgtYFTCQZz8DAMAg1sCBzCloXYFWEHCosuCRo46TSzsVYFXWXx2LXgCxBNScNQg2MpCZvADOPDisCBAgVAAAAAElFTkSuQmCC",V="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABEAAAAPCAIAAACN07NGAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAARUlEQVQokWP8//4qAwPD2S8KDEQDJuKVjupBAixYRWd1NxzYsRHOdfDwTyttIGBPWmmDg4c/Vg047YFoQ2PAAeNoOmAAAKfBFoOUdbkfAAAAAElFTkSuQmCC",P="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAPCAIAAABiEdh4AAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAM0lEQVQokWP8//4qAymAiSTVoxogYPbCNZ0T5mJlQAALmoYLl2+8e/8RKwMCGEcjjggAAD3oIFrtsIcuAAAAAElFTkSuQmCC",x="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.397-DHmf_7Y3.png",U="data:image/png;base64,",z="/parts-intellect-docs/assets/Aspose.Words.83ab1c44-6b28-430a-a5f2-4d9e6ba1abd4.399-CEt5oYxF.png",D="data:image/png;base64,",N="data:image/png;base64,",J="data:image/png;base64,",O="data:image/png;base64,",K="data:image/png;base64,",M="data:image/png;base64,",T="data:image/png;base64,",X="data:image/png;base64,",Y="data:image/png;base64,",b={};function L(G,t){return B(),d("div",null,t[0]||(t[0]=[C('<p>Для поиска и оценки стоимости товара в программе существует такой инструмент как <strong>Проценка</strong>.</p><p>Данный инструмент позволяет найти товар в выбранных источниках и вычислить его стоимость для клиента. Процедура поиска состоит из нескольких этапов.</p><p>Отдельно необходимо упомянуть о быстродействии инструмента. Одним из этапов улучшения скорости работы <strong>Проценки</strong> был этап &quot;запоминания&quot; (кэширования) требуемых для ее работы справочников и опций.</p><p>Например, если открыть окно <strong>Проценки</strong>, затем задать округление цен для валют и вернуться в <strong>Проценку</strong>, то мы не увидим изменений. Необходимо закрыть окно <strong>Проценки</strong>, а потом открыть <strong>Проценку</strong> заново, в этом случае мы увидим изменения.</p><p>На первом этапе вызывается <strong>Проценка</strong> (горячая клавиша <strong>F12</strong>) и в строку поиска вводится искомая позиция (допускается поиск нескольких товаров, разделенных &#39;;&#39;).</p><p><img src="'+Q+'" alt=""></p><p><img src="'+A+'" alt=""> <strong>Опции поиска</strong></p><p><img src="'+w+'" alt=""></p><p>Позволяет указать какие источники поиска необходимо использовать при проценке.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Перечень доступных для пользователя источников поиска в проценке определяется разрешениями в меню <strong>Управление ► Настройки программы ► Роли пользователей ► Прайс-листы ► Проценка</strong>.</p></div><p>Инструмент проценки позволяет осуществлять поиск в следующих источниках:</p><ul><li><strong>В наличии</strong> – поиск предложений осуществляется среди позиций доступных пользователю <strong>Прайс-листов из наличия</strong>;</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Видимость скрытых прайс-листов определяется настройкой <strong>Видны скрытые прайс-листы</strong> в разрешениях пользователя в разделе меню <strong>Управление ►</strong> <strong>Настройки программы ► Роли пользователей ► Прайс-листы ► Прайс-листы из наличия</strong>.</p></div><ul><li><p><strong>Под заказ</strong> – поиск предложений осуществляется среди позиции загруженных в программу <strong>Прайс-листов от поставщиков</strong>;</p></li><li><p><strong>В веб-проценке</strong> – поиск предложений осуществляется среди подключенных в программе <strong>Веб-прайс-листов от поставщиков</strong>;</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>В веб-прайс-листах доступен поиск только &quot;<strong>1. По коду</strong>&quot; и действует только строгое совпадение.</p></div><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>При активной настройке <strong>Исключать повторные предложения от одного веб-поставщика</strong> (раздел <strong>Управление ► Настройки программы ► Настройки</strong>, группа <strong>Проценка ► Настройки поиска</strong>), если на втором шаге проценки от одного веб-поставщика найдено несколько предложений с одинаковыми значениями полей: Код, Производитель, Направление/Склад, Срок поставки, Цена (поставщика), то в результатах поиска будет отображено только первое предложение из подобранных.Видимость скрытых прайс-листов определяется настройкой <strong>Видны скрытые прайс-листы</strong> в разрешениях пользователя в разделе меню <strong>Управление ►</strong> <strong>Настройки программы ► Роли пользователей ► Прайс-листы ► Прайс-листы от поставщиков</strong> и <strong>Веб-прайс-листы от поставщиков</strong>.</p></div><ul><li><p><strong>В синонимах</strong> – поиск предложений осуществляется с учетом позиций справочника <strong>Синонимы товаров</strong>;</p></li><li><p><strong>В аналогах</strong> – поиск предложений осуществляется с учетом позиций справочника <strong>Аналоги</strong>;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Поиск в справочнике аналогов осуществляется с учетом значения настройки <strong>Глубина поиска аналогов</strong> в разделе меню <strong>Управление ► Настройки программы ► Настройки,</strong> группа <strong>Проценка</strong> ► <strong>Аналоги</strong>.</p></div><ul><li><p><strong>В оригинальных заменах</strong> – поиск осуществляется среди позиций справочника <strong>Оригинальные замены производителей</strong>;</p></li><li><p><strong>В веб-аналогах</strong> – поиск аналогов на запрошенный артикул будет осуществляться в базе сервиса <strong>Веб-аналоги</strong>, который предназначен для дополнения или замещения локальной базы аналогов (кроссов) программы. Данная возможность значительно расширяет вашу базу аналогов, что повышает результативность поиска аналогов для запчастей;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Поиск в сервисе веб-аналоги осуществляется с учетом значения настройки Глубина поиска <strong>Уровень доверия к аналогам</strong> в разделе меню <strong>Управление ► Настройки программы ► Настройки,</strong> группа <strong>Проценка</strong> ► <strong>Аналоги</strong>.</p></div><ul><li><p><strong>В заказе поставщику</strong> – данный источник позволяет искать детали, которые &quot;едут на склад&quot; и находятся в заказе поставщику;</p></li><li><p><strong>В аналогах из веб-проценки</strong> – данный источник используется для поиска аналогов у веб-поставщиков (среди позиций, подключенных веб-прайс-листов). Для поиска аналогов с помощью проценки в веб-прайс-листе необходимо настроить эту возможность в карточке веб-прайс-листа).</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Подключение веб-сервисов <strong>Веб-проценки</strong>, <strong>Веб-заказ</strong> и <strong>Веб-аналоги</strong> осуществляется по запросу. Условия подключения можно узнать у наших менеджеров (контактные данные можно найти по адресу <a href="http://www.tradesoft.ru/about/contacts/" target="_blank" rel="noopener noreferrer">www.tradesoft.ru/about/contacts</a>).</p></div><p><img src="'+o+'" alt=""> <strong>Настройки</strong></p><p><img src="'+I+'" alt=""></p><p>Позволяет задать следующие параметры поиска, для утонения результатов поиска:</p><ul><li><p><strong>Клиент</strong> – с помощью данного параметра автоматически определяется <strong>Категория клиента</strong>, а при формировании документов на основании процененных товаров, выбранный клиент будет автоматически подставляться в документ;</p></li><li><p><strong>Производитель</strong> – заданный производитель товара, позволяет сократить область поиска на первом шаге проценки;</p></li><li><p><strong>Категория клиента</strong> – в зависимости от выбора категории будет применять тот или иной уровень цен (маркетинг) на искомые позиции согласно алгоритму по подбору маркетингов;</p></li><li><p><strong>Валюта</strong> – выберите валюту в которой необходимо отобразить цены в результатах поиска;</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Если валюта источника и валюта отображения различны, то между ними обязательно должен быть задан курс в справочнике <strong>Валюты и курсы</strong>.</p></div><ul><li><p><strong>Способ группировки</strong> – опция позволяет сгруппировать результаты поиска. Возможные варианты:</p><ul><li><p><strong>Без группировки</strong> – в этом случае группировку можно либо совсем не использовать, либо группировать вручную штатными средствами по имеющимся колонкам;</p></li><li><p><strong>Наличие, в пути, под заказ</strong> – при данном способе группировке в первой группе будет отображено все &quot;наличие&quot; (запрошенные товары и их аналоги), затем товары &quot;в пути&quot; (если включен источник <strong>В заказе поставщику</strong>), затем товары &quot;под заказ&quot; (запрошенные товары и их аналоги). В конце секция с отсутствующими на складах товарами;</p></li><li><p><strong>Запрошенный, аналоги</strong> – в первой группе будут запрошенные товары &quot;в наличии&quot;, затем аналоги &quot;в наличии&quot;, далее запрошенные товары &quot;в пути&quot;, затем аналоги &quot;в пути&quot;, далее запрошенные товары &quot;под заказ&quot;, затем аналоги &quot;под заказ&quot;. В конце секция с отсутствующими на складах товарами.</p></li></ul></li><li><p><strong>Группа прайс-листов</strong> - опция позволяет выполнить поиск только по прайс-листам, относящимся к выбранной группе. Значение <strong>(Все группы прайс-листов)</strong> позволяет выполнять поиск без учета групп.</p></li></ul><p><img src="'+s+'" alt=""> <strong>Вкладки поиска</strong></p><p><img src="'+h+'" alt=""></p><p>Позволяет осуществить выбор вкладки поиска, по которой будет осуществляться поиск товара. Доступны параметры поиска:</p><ul><li><p><strong>По коду</strong> – позволяет осуществить проценку товара по артикулу;</p></li><li><p><strong>По названию</strong> – позволяет выполнить проценку товара используя его наименование;</p></li><li><p><strong>По внутреннему коду</strong> – позволяет проценить товар по его внутреннему коду из карточки товара;</p></li><li><p><strong>По штрих коду</strong> – позволяет осуществить проценку товара по его штрихкоду.</p></li></ul><p>Кнопка <img src="'+f+'" alt=""> позволяет вернуться к шагу выбора производителя.</p><p><img src="'+r+'" alt=""> <strong>Строка поиска</strong></p><p><img src="'+R+'" alt=""></p><p>Позволяет определить режим вхождения поискового запроса:</p><p><img src="'+S+'" alt=""> – поиск по началу (например, если строкой поиска является &quot;123&quot;, то будут найдены все записи, начинающиеся с 123: <strong>123</strong>4, <strong>123</strong>555, <strong>123</strong>00 и т.д.),</p><p><img src="'+v+'" alt=""> – поиск по вхождению (например, если строкой поиска является &quot;123&quot;, то будут найдены все записи, содержащие 123: 00<strong>123</strong>4, <strong>123</strong>555, 0113<strong>123</strong> и т.д.),</p><p><img src="'+q+'" alt=""> – поиск по строгому совпадению (например, если строкой поиска является &quot;123&quot;, то будут найдены записи, полностью совпадающие с 123: <strong>123</strong>).</p><p>Позволяет ввести искомый параметр или выбрать его из истории поиска <img src="'+V+'" alt=""> или справочника товаров <img src="'+P+'" alt="">. Запуск поиска осуществляется по кнопке <strong>Найти</strong> или клавишей <strong>Enter</strong> клавиатуры.</p><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Если в поисковую строку одновременно введены русские и латинские буквы, строка окрашивается в розовый цвет, тем самым уведомляя о возможной ошибке в веденном искомом параметре.</p></div><p><img src="'+n+'" alt=""> <strong>Результаты поиска</strong></p><p><img src="'+x+'" alt=""></p><p>Позволяет выполнить фильтрацию результатов поиска по типу предложения:</p><ul><li><p><strong>все</strong> - отображает полный список результатов поиска;</p></li><li><p><strong>запрошенные</strong> - отображает результаты по запрошенному артикулу;</p></li><li><p><strong>оригинальные замены</strong> - отображает результаты по оригинальным заменам;</p></li><li><p><strong>аналоги</strong> - отображает результаты по аналогам;</p></li><li><p><strong>наличие по складам</strong> - отображает предложения в наличие на складах и их количество;</p></li><li><p><strong>без цены</strong> - отображает предложения без цены.</p></li></ul><p><img src="'+g+'" alt=""> <strong>Строка состояния</strong></p><p><img src="'+U+'" alt=""></p><p>Позволяет осуществить проценку в соответствии с заданными параметрами поиска.</p><p><img src="'+i+'" alt=""> <strong>Табличная часть</strong></p><p><img src="'+z+'" alt=""></p><p>Табличная часть содержит колонки:</p><ul><li><p><strong>ID товара</strong> – Oid товара согласно справочнику <strong>Товаров</strong> (для позиций, отсутствующих в справочнике, значение -1);</p></li><li><p><strong>Альтернативное</strong> <strong>название</strong> <strong>товара</strong> – значение поля <strong>Альтернативное название</strong> из карточки товара (для позиций, присутствующих в справочнике <strong>Товаров</strong>);</p></li><li><p><strong>Валюта</strong> – валюта прайс-листа-источника позиции или значения в <strong>Параметрах проценки</strong> (для позиций из документов не отображается);</p></li><li><p><strong>Веб-инфо</strong> – при нажатии на иконку <img src="'+D+'" alt=""> в колонке, есть возможность получить более расширенную информацию о детали. При нажатии на эту иконку программа запрашивает товар в сервисе Веб-инфо и, если там найден этот товар, отображает окно с информацией о нем;</p></li><li><p><strong>Вес</strong> – значение веса детали (если указано в источнике);</p></li><li><p><strong>Внутренний код</strong> – значение поля <strong>Внутренний код</strong> из справочника <strong>Товаров</strong> или из Прайс-листа от поставщика;</p></li><li><p><strong>Внутренний резерв</strong> – отображает время до окончания <strong>Внутреннего резерва</strong> по позиции (<strong>Управление ► Настройки программы ► Настройки ►</strong> группа <strong>Склад и закупки ► Движение товара</strong>);</p></li><li><p><strong>Возможность возврата</strong> – отображает признак возможности возврата для прайс-листа, либо заданный в настройках прайс-листа, либо переданный от веб-поставщика;</p></li><li><p><strong>Время заказа</strong> – данная колонка отображает ближайшее доступное время отправки заказа поставщику, согласно <strong>Графику поставок</strong>;</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Подробнее о работе с модулем <strong>Графики поставок</strong> читайте в <a href="https://product-doc.tradesoft.ru/ai/delivery_schedule/index.htm" target="_blank" rel="noopener noreferrer">руководстве пользователя</a>.</p></div><ul><li><p><strong>Группа товара</strong> – отображает основную группа товара по справочнику <strong>Товаров</strong>;</p></li><li><p><strong>Дополнительные свойства товара</strong> – дополнительные свойства из карточки товара;</p></li><li><p><strong>Доставка/Срок</strong> – срок поставки детали. Если в результатах проценки присутствуют сроки, заданные диапазоном, то сортировка производится по максимальному значению диапазона. Например, в результатах поиска позиции со сроками в днях: 2-5, 1, 4, 6-7. При сортировке позиции отобразятся в последовательности 1, 4, 2-5, 6-7. Позиции с не числовым сроком при сортировке отображаются в конце списка;</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Срок поставки детали берется с учетом значения в маркетинге <strong>Конечный срок доставки до клиента</strong>:</p><ul><li><strong>Оригинальная строка из прайс-листа</strong>. В поле &quot;Срок доставки&quot; будет передан срок доставки в том виде, в котором он задан в прайс-листе.</li><li><strong>Оригинальная строка из маркетинга</strong>. В поле &quot;Срок доставки&quot; будет передан срок доставки в том виде, в котором он задан в Маркетинге.</li><li><strong>Обработанная строка из прайс-листа</strong>. В поле &quot;Срок доставки&quot; будет передан срок доставки из прайс-листа в обработанном виде.</li><li><strong>Обработанная строка из маркетинга</strong>. В поле &quot;Срок доставки&quot; будет передан срок доставки из Маркетинга в обработанном виде.</li><li><strong>Сумма сроков из прайс-листа и маркетинга</strong>. В поле &quot;Срок доставки&quot; будет передана сумма сроков по доставке из прайс-листа и маркетинга.</li></ul></div><div class="hint-container note"><p class="hint-container-title">Пример</p><ul><li>доставка в прайс-листе &quot;1 день&quot;, в маркетинге &quot;3дн&quot;, итоговая доставка &quot;4&quot;;</li><li>доставка в прайс-листе &quot;1-11дн&quot;, в маркетинге &quot;3 дня&quot;, итоговая доставка &quot;4-14&quot;;</li><li>доставка в прайс-листе &quot;от 1 до 11 дней&quot;, в маркетинге &quot;2-12дн&quot;, итоговая доставка &quot;3-23&quot;.</li></ul><p>Программа всегда измеряет срок доставки в днях. В случае, если в прайс-листе или маркетинге задан срок доставки &quot;2 месяца&quot;, то он будет преобразован в &quot;2&quot;, что повлечет искажение информации.</p><p>Если срок доставки указан в виде нестандартной строки (&quot;неделя&quot;, &quot;два дня&quot;, &quot;месяц&quot; и т.д.), то будет передано пустое значение. К данным срокам поставки может быть прибавлен срок, заданный в справочнике <strong>Доставки по направлениям и складам</strong>.</p></div><ul><li><p><strong>Доставка: закуп</strong> – значение из маркетинга <strong>Цена доставки</strong> для закупа;</p></li><li><p><strong>Доставка: продажа</strong> – значение из маркетинга <strong>Цена доставки</strong> для продажи;</p></li><li><p><strong>Закупочная цена</strong> – цена закупа (поставщика) из прайс-листа с учетом маркетинга;</p></li><li><p><strong>Залоговая стоимость</strong> – значение поля Залоговая стоимость из прайс-листа от поставщика;</p></li><li><p><strong>Изображения товара</strong> – кнопка – ссылка на изображения товара из карточки товара;</p></li><li><p><strong>Источник</strong> – источник позиции:</p><ul><li><p><strong>в наличии</strong> – позиция из прайс-листа из наличия;</p></li><li><p><strong>в резерве</strong> – позиция на складе, но зарезервирована для другого клиента;</p></li><li><p><strong>во внутреннем резерве</strong> – позиция находится в временном резерве (см. колонку <strong>Внутренний резерв</strong>);</p></li><li><p><strong>под заказ</strong> – позиция из прайс-листа от поставщика;</p></li><li><p><strong>веб-проценка</strong> – позиция из веб-прайс-листа от поставщика;</p></li><li><p><strong>в заказе для клиента</strong> – позиция находится в заказе поставщику, но едет под заказ клиента;</p></li><li><p><strong>в заказе на склад</strong> – позиция находится в заказе поставщику и едет под <strong>Наш заказ</strong> <strong>на склад</strong>;</p></li></ul></li><li><p><strong>Исходный</strong> <strong>производитель</strong> – наименование производителя в том виде, как оно записано в источнике поиска;</p></li><li><p><strong>Код</strong> – артикул детали;</p></li><li><p><strong>Название</strong> – наименование товара;</p></li><li><p><strong>Наличие</strong> – количество товара в источнике;</p></li><li><p><strong>Кратность наличия</strong> – коэффициент, согласно которому может быть выбрано количество для заказа из наличия. Также выполняет роль минимального количества к заказу;</p></li><li><p><strong>Кратность от поставщика</strong> – коэффициент, согласно которому может быть выбрано количество для заказа от поставщика. Также выполняет роль минимального количества к заказу;</p></li><li><p><strong>Наценка, %</strong> – наценка на основании Цены продажи и Закупочной цены на товар;</p></li><li><p><strong>Обновлено</strong> – дата последнего обновления по позиции из прайс-листа;</p></li><li><p><strong>Обработанный производитель</strong> – наименование производителя с учетом обработки через справочник <strong>Синонимы производителей</strong>;</p></li><li><p><strong>Описание</strong> – описание источника поиска;</p></li><li><p><strong>Описание</strong> <strong>товара</strong> – описание из карточки товара;</p></li><li><p><strong>Плановая дата поставки</strong> – данная колонка отображает ближайшую дату и время отгрузки детали от поставщика при соблюдении <strong>Времени заказа</strong>, согласно <strong>Графику поставок</strong>;</p></li></ul><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>Подробнее о работе с модулем <strong>Графики поставок</strong> читайте в <a href="https://product-doc.tradesoft.ru/ai/delivery_schedule/index.htm" target="_blank" rel="noopener noreferrer">руководстве пользователя</a>.</p></div><ul><li><strong>Направление/Склад</strong> – направление поставки или склад из прайс-листа;</li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Из выпадающего списка в колонке <strong>Направление/Склад</strong> доступен выбор <strong>Нашей фирмы</strong> и <strong>Места хранения</strong> (для товаров из наличия), с которых необходимо списать товар. Выбранная <strong>Наша фирма будет</strong> отображаться:</p><ul><li>в колонке <strong>Наша фирма</strong> в окне <strong>Выбранные позиции</strong>;</li><li>в шапке созданного документа, поле <strong>Наша фирма</strong>.</li></ul><p>Выбранное <strong>Место хранения</strong> будет отображаться:</p><ul><li>в поле <strong>Приоритетный склад для списания</strong> в табличной части созданного документа.</li></ul><p>Применяется для документов: <strong>VIN-запрос</strong>, <strong>Заказ клиента</strong>, <strong>Отгрузка на реализацию</strong>, <strong>Расходная накладная</strong>, <strong>Резервирование</strong>. Выбор товара из выпадающего списка доступен при задании настройки <strong>Разделять позиции по НФ при создании документа из проценки</strong> в значение <strong>Автовыбор НФ</strong> (раздел <strong>Управление ► Настройки программы ► Настройки ► Документы</strong>).</p></div><ul><li><p><strong>Наша фирма</strong> – фирма, на которую оприходован товар, отображается только для <strong>Партионной СЦО</strong>;</p></li><li><p><strong>Поставщик</strong> – поставщик из маркетинга, который применился к позиции;</p></li><li><p><strong>Прайс-лист</strong> – наименование прайс-листа из которого отображается позиция;</p></li><li><p><strong>Рейтинг поставщика</strong> – рейтинг поставщика (только по данным веб-прайс-листов);</p></li><li><p><strong>Розница</strong> – цена продажи для категории клиентов Розница (если есть маркетинг для данной категории);</p></li><li><p><strong>Статистика поставок</strong> – гиперссылка для просмотра <strong>Статистики поставки товара поставщиками</strong> (разделе меню <strong>Управление ► Настройки программы ► Настройки ►</strong> группа <strong>Склад и закупки ► Закупки</strong>);</p></li><li><p><strong>Страна-изготовитель</strong> – одноименное значение из карточки товара;</p></li><li><p><strong>Тип позиции</strong> – если в качестве источника поиска выбрано <strong>В оригинальных заменах</strong>, то по колонке <strong>Тип позиции</strong> в результатах поиска можно уточнить, текущая позиция относится к аналогам, устаревшему или новому коду в случае наличия оригинальных замен и аналогам или запрошенному коду в случае отсутствия оригинальных замен;</p></li><li><p><strong>Цена продажи</strong> – цена на товар с учетом маркетинга;</p></li><li><p><strong>Штрихкод производителя</strong> – штрихкод производителя из источника позиции;</p></li><li><p><strong>Минимальная партия от поставщика</strong> – минимальное количество единиц товара под заказ в одной партии.</p></li></ul><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Чтобы настроить разрешение на проведение документа с количеством меньше минимальной партии, в разделе <strong>Управление ► Настройки программы</strong>, вкладка <strong>Роли пользователей ► Разрешения для роли пользователей,</strong> группы настроек <strong>Заказы клиентов, Наши заказы на склад, Заказы поставщикам, VIN-запросы</strong> воспользуйтесь настройкой <strong>Можно проводить с количеством меньше минимальной партии поставщика</strong> для нужных документов и пользователей.</p></div><p>Для того, чтобы отобразить/скрыть колонку необходимо на заголовке любой колонки, путем нажатия правой кнопкой мыши, через контекстное меню вызвать команду <strong>Выбор колонок</strong>. В окне Выбора колонок доступен поиск нужной колонки, поиск осуществляется по вхождению строки. Для выбора установите у соответствующих колонок отметки, после чего закройте окно.</p><p><img src="'+p+'" alt=""> <strong>Фильтры и обработки результатов поиска</strong></p><p><img src="'+N+'" alt=""></p><p>Позволяет выполнить фильтрацию и обработку результатов поиска.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Возможность изменять фильтры проценки для пользователя определяется значением настройки <strong>Разрешения для фильтров и обработок в проценке</strong> в разделе меню <strong>Управление ► Настройки программы ► Роли пользователей ► Прайс-листы ► Проценка</strong>.</p></div><p><img src="'+l+'" alt=""> <strong>Расцветка результатов поиска</strong></p><p><img src="'+J+'" alt=""></p><p>Позволяет выоплнить расцвечивание (выделение) результатов поиска.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Возможность изменять расцветки проценки для пользователя определяется значением настройки <strong>Разрешения для расцветок проценки</strong> в разделе меню <strong>Управление ► Настройки программы ► Роли пользователей ► Прайс-листы ► Проценка</strong>.</p></div><p><img src="'+a+'" alt=""> <strong>Добавить</strong></p><p><img src="'+O+'" alt=""></p><p>Позволяет осуществить выбор выделенных позиций из результатов поиска для последующего создания документов с ними. После нажатия кнопки выбранные позиции переместятся в список <strong>Выбранные позиции</strong> окна <strong>Проценки</strong>.</p><p><img src="'+e+'" alt=""> <strong>Удалить</strong></p><p><img src="'+K+'" alt=""></p><p>Позволяет произвести удаление позиции из списка <strong>Выбранные позиции</strong> окна <strong>Проценки</strong>:</p><ul><li><p><strong>Удалить</strong> – удаление выбранных позиций;</p></li><li><p><strong>Удалить все</strong> – удаление всех позиций.</p></li></ul><p><img src="'+c+'" alt=""> <strong>Создать</strong></p><p><img src="'+M+'" alt=""></p><p>Если работа с проценкой окончена, то с товарами в списке <strong>Выбранные позиции</strong> можно выполнить действия через команду <strong>Создать</strong>. Команда содержит следующие доступные действия:</p><ul><li><strong>VIN-запрос</strong> – для выбранных позиций создается документ <strong>VIN-запрос</strong>;</li><li><strong>Заказ клиента</strong> – если товара нет в наличии, но есть под заказ, то оформляется документ <strong>Заказ клиента</strong>;</li><li><strong>Наш заказ</strong> – для формирования заказа товара у поставщиков от лица нашей фирмы создается документ <strong>Наш заказ на склад</strong>;</li><li>**Отгрузка на реализацию ** – выбранный товар уйдет в документ <strong>Отгрузка на реализацию</strong>;</li><li><strong>Расходная накладная</strong> – если процененный товар есть в наличии, то оформляется <strong>Расходная накладная</strong>;</li><li><strong>Резервирование</strong> – для резервирования выбранного товара за клиентом оформляется документ <strong>Резервирование</strong>;</li><li><strong>Заказ поставщику</strong> – заказ процененных товаров у поставщика через документ <strong>Заказ поставщику</strong>;</li><li><strong>Приходная накладная</strong> – создается документ <strong>Приходная накладная</strong>;</li><li><strong>Перемещение</strong> – будет создан документ <strong>Перемещение</strong> для выбранных позиций проценки;</li><li><strong>Заказ-Наряд</strong> – будет создан документ <strong>Заказ-Наряд</strong> и на вкладку с товарами будут добавлены выбранные позиции проценки;</li><li><strong>Новый товар</strong> – добавление нового товара в справочник товаров. В поле наименование товара передается строка поиска из <strong>Проценки</strong>;</li><li><strong>Списать товар</strong> – будет создан документ <strong>Списание товара</strong> для выбранных позиций проценки;</li><li><strong>Списать товар</strong> – будет создан документ <strong>Списание товара</strong>;</li><li><strong>Очистить позиции</strong> – если опция включена, то после создания документа будет очищаться список выбранных позиций. Иначе – нет;</li><li><strong>Сбросить опции поиска</strong> – если опция включена, то после создания документа опции поиска проценки будут сброшены в стандартное состояние;</li><li><strong>Для выделенных товаров</strong> – если опция включена, то в создаваемый документ будут переданы только выделенные позиции, иначе – все;</li><li><strong>Удалить позиции после создания</strong> – это опция, позволяющая указать программе, очищать или нет список выбранных позиций в проценке после создания документа.</li></ul><p><img src="'+m+'" alt=""> <strong>Вставить</strong></p><p><img src="'+T+'" alt=""></p><p>Применяется для вставки выбранного предложения в документ, когда проценка выполнялась из инспектора документа.</p><p><img src="'+u+'" alt=""> <strong>Выбранные позиции</strong></p><p><img src="'+X+'" alt=""></p><p>Выбранные детали в <strong>Табличной части</strong> после нажатия кнопки <strong>Добавить</strong> отобразятся в списке <strong>Выбранные позиции</strong>. При повторном добавлении позиции в список <strong>Выбранных позиций</strong> происходит увеличение количества товара на единицу.</p><div class="hint-container note"><p class="hint-container-title">Замечание</p><p>Если включен механизм <strong>Внутреннего резервирования товаров</strong> (разделе меню <strong>Управление ► Настройки программы ► Настройки ►</strong> группа <strong>Склад и закупки ► Движение товара</strong>), то при добавлении позиции в список выбранных она резервируется за пользователем. Тем самым позицию никто, кроме этого пользователя, не сможет продать в отведенное под резерв время.</p></div><div class="hint-container info"><p class="hint-container-title">Примечание</p><p>В разделе <strong>Проценка</strong> доступно интерактивное обучение, которое появляется по нажатию на кнопку <img src="'+Y+'" alt="">. В ходе обучения в пустом интерфейсе поочередно подсвечиваются основные блоки, рядом выводится текст с кратким описанием назначения блока. При этом неактивные блоки затемняются. В конце <strong>Интерактивного обучения</strong> рассматривается работа <strong>Проценки</strong> на примере. Рядом с каждым подсвеченным блоком отображается текстовое окно с подсказкой. На последнем шаге обучения пользователю выводится окно со ссылкой на видео по работе в <strong>Проценке</strong>, по загрузке различных прайс-листов и настройке цен для этих прайс-листов. Наличие интерактивного обучения определяется настройкой <strong>Отображать иконку интерактивного обучения в проценке</strong> в разделе Управление <strong>► Настройки программы ► Пользователи ► Настройки пользователя ►</strong> вкладка <strong>Проценка</strong>.</p></div>',90)]))}const nt=E(b,[["render",L]]),gt=JSON.parse('{"path":"/specification/prodazhi/protsenka.html","title":"Проценка","lang":"en-US","frontmatter":{"title":"Проценка","createTime":"2025/04/22 21:20:27"},"headers":[],"readingTime":{"minutes":9.83,"words":2949},"git":{"updatedTime":1745415005000,"contributors":[{"name":"a.lutoshkin","username":"","email":"a.lutoshkin@tradesoft.ru","commits":1,"avatar":"https://gravatar.com/avatar/605ee9d365e3a6380f8e9c71f9269ee5373d537aebefbece4e023cac396dfc8d?d=retro"}]},"filePathRelative":"specification/prodazhi/protsenka.md","categoryList":[{"id":"fd8b54","sort":10022,"name":"specification"},{"id":"951041","sort":10039,"name":"prodazhi"}]}');export{nt as comp,gt as data};
